css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rainbowdash.net") {
    @media all {
        #wrap {
            width: 87% !important;
            min-width: 840px;
            max-width: 1380px;
            background-size: contain !important;
        }
        
        #header {
            width: auto !important;
            margin: 0 7px !important;
        }
        
        address {
            max-width: 67% !important;
            overflow-x: hidden !important;
        }
        
        #site_nav_global_primary {
            width: 100% !important;
            left: -25px !important;
            padding-right: 25px !important;
            padding-left: 25px !important;
        }
        
        #site_nav_global_primary ul {
            width: 100% !important;
            margin: 0 -17px !important;
        }
        
        #site_nav_global_primary ul > :last-child {
            margin-right: -34px !important;
        }
        
        #site_notice {
            width: 30% !important;
            -moz-box-sizing: border-box;
            -webkit-box-sizing: border-box;
            box-sizing: border-box;
        }
        
        #form_notice {
            margin: 0 !important;
            margin-bottom: 30px !important;
            width: 69% !important;
        }
        
        #core {
            width: auto !important;
            margin: 0 8px !important;
        }
        
        #site_nav_local_views {
            width: 28% !important;
            -moz-box-sizing: padding-box;
            -webkit-box-sizing: padding-box;
            box-sizing: padding-box;
        }
        
        #site_nav_local_views a {
            margin-left: -12% !important;
        }
        
        #content {
            width: 68% !important;
            padding-right: 1%;
            padding-left: 1%;
            z-index: 10 !important;
        }
        
        #aside_primary {
            width: 29% !important;
        }
        
        #aside_primary .section {
            width: 100% !important;
            -moz-box-sizing: padding-box;
            -webkit-box-sizing: padding-box;
            box-sizing: padding-box;
            margin-right: 0 !important;
            padding-left: 0 !important;
            padding-right: 5px !important;
        }
        
        .filmstrip {
            -moz-box-sizing: padding-box;
            height: 75px !important;
        }
    }
    
    /*[[widescreen]]*/
}

style_setting_options.value:
@media all AND (min-width: 1300px) {
        #form_notice {
            width: 59.5% !important;
            margin-top: -20px !important;
            margin-left: 15% !important;
            padding-left: 8px !important;
            padding-right: 8px !important;
            -moz-box-sizing: padding-box;
            -webkit-box-sizing: padding-box;
            box-sizing: padding-box;
        }
        
        #form_notice input.submit {
            top: 126px !important;
        }
        
        #form_notice textarea {
            height: 100px !important;
        }
        
        #site_notice {
            width: 25% !important;
        }
        
        address {
            max-width: 110px !important;
            overflow-x: hidden;
            position: absolute;
            left: 20px;
        }

        #site_nav_local_views {
            width: 24% !important;
        }
        
        #site_nav_local_views li:first-child a {
            border-top-left-radius: 4px !important;
        }
        
        #content {
            width: 58% !important;
            margin-left: 15% !important;
            border-top-right-radius: 4px !important;
        }
        
        #aside_primary {
            width: 100% !important;
            float: none !important;
            padding: 0 !important;
            margin: 0 !important;
        }
        
        #aside_primary .section {
            width: 24% !important;
            clear: none !important;
        }
        
        #aside_primary .ad .section,
        #aside_primary .section#affilliate_section,
        #aside_primary .section#cornify_section,
        #aside_primary .section#link_section {
            width: 14% !important;
            position: absolute !important;
            left: -3px !important;
            margin-top: 5px !important;
        }
        #affilliate_section {top: 170px !important}
        #cornify_section {top: 565px !important}
        #link_section {top: 605px !important}
        
        #affilliate_section img {max-width: 100% !important}
    }
