css:
@-moz-document domain("hatenablog.com"), domain("hatenablog.jp"), domain("hatenadiary.jp"), domain("hateblo.jp"), domain("hatenadiary.com") {
#top-editarea, #bottom-editarea, .sleeping-ads, .sleeping-ad {
  display: none !important;
  padding: 0 !important;
  margin: 0 !important;
  height: 0 !important;
  min-height: inherit !important;
}

.page-index, .page-entry, .page-entries-year-month-day, .page-archive, .page-about, .page-search {
  background-image: none;
}

body {
  font-feature-settings: "palt";
  letter-spacing: .3pt;
  -webkit-font-smoothing: antialiased;
}

#globalheader-container {
  border: 0;
  background-color: #bbb;
}

#blog-title, #blog-title * {
  margin: 0 auto;
  width: auto;
  background: none;
  border: 0;
  min-height: inherit;
}
#blog-title:before {
  background: none;
  width: auto;
  height: auto;
}
#title {
  font-size: 20px;
}
header * {
  text-shadow: none !important;
}

hr.titlestyle {
  border: 0;
}

body > #container {
  width: 800px !important;
  background: none;
  margin: 0 auto;
}

#container {
  width: auto;
  border: 0;
}
#container * {
  box-sizing: border-box;
}

#container-inner {
  width: auto;
  margin: 0 auto;
  padding: 0;
  background: none;
}

#blog-title {
  height: auto !important;
  padding: 20px 0 10px;
  color: inherit;
  text-shadow: none;
  border: 0 !important;
}

#blog-title * {
  color: inherit !important;
}

#blog-title-inner {
  width: auto !important;
  text-align: center;
  height: auto !important;
  margin: 0 auto !important;
  padding: 2em;
}

#title a {
  font-size: 24px;
  color: inherit;
}

.date {
  position: static;
  width: auto;
  height: auto;
  background: none;
  font-size: 16px;
  border: 0;
  opacity: 1;
  text-align: right !important;
}
.date:before {
  content: none;
}
.date * {
  display: inline !important;
  position: static !important;
  top: inherit !important;
  left: inherit !important;
  padding: 0 !important;
  background: none !important;
  font-size: 14px !important;
  color: #aaa !important;
  border: 0 !important;
  font-family: sans-serif !important;
}
.date *:after {
  content: none !important;
}

.headlink {
  text-align: center;
}

.headlink, .headlink a {
  color: inherit;
}

#container #content {
  width: auto;
  min-width: inherit;
  margin: 0;
  padding: 0;
  background: none;
  border: 0 !important;
  box-shadow: 0 1px 2px #ddd !important;
  text-shadow: none;
}

#content-inner {
  display: block;
  width: auto !important;
  margin: 0 auto;
  background-color: #fff;
  padding: 40px 40px;
  border-radius: 4px;
}

#wrapper, #main, aside, aside>div, .hatena-module {
  width: auto !important;
  float: none !important;
  position: static;
  border: 0;
  margin-left: auto;
  margin-right: auto;
  padding: 0;
}

#wrapper {
  display: block;
  padding: 0;
  background: none;
  box-shadow: none;
}
#main {
  box-shadow: none;
  background: none !important;
}
#main-inner {
  padding: 0;
  border: 0;
}

aside#box2 {
  display: block;
  margin-top: 40px;
  padding-top: 40px;
  border-top: 1px dashed #ddd;
  position: relative;
}

article, .entry {
  margin: 0;
  padding: 0 0 200px;
  border: 0 !important;
  box-shadow: none;
  color: #333;
  background-color: transparent;
}

.entry-header {
  margin: 0 0 20px;
  padding: 0;
  width: auto;
  left: inherit;
}

.entry-title {
  text-align: center;
}
.entry-title a {
  font-size: 20px;
  color: #333;
}

.entry-inner {
  width: auto;
  padding: 0;
}

.entry-content {
  width: auto;
  max-width: 720px;
  position: static;
  font-family: inherit;
  font-size: 16px;
  background-color: #fff;
}

.entry-content h1, .entry-content h2, .entry-content h3, .entry-content h4 {
  box-shadow: none;
}

.entry-content h2 {
  margin-top: 80px;
  border: 0;
}

.entry-content h3 {
  margin-top: 60px;
  border: 0;
  border-bottom: 1px solid #ccc;
  color: #666;
}

.entry-content h4 {
  margin-top: 30px;
}
.entry-content img {
  box-shadow: 0 1px 3px #ddd;
  border-radius: 3px !important;
}

.keyword {
  color: inherit !important;
  border: 0 !important;
  text-decoration: none !important;
}

blockquote {
  padding: 20px 20px 20px 40px !important;
  background: none !important;
  background-color: #fafafa;
  border: 0 !important;
  border-left: 4px solid #eee !important;
}
blockquote:before {
  color: #ddd;
  content: "“";
  font: 60px/1 Georgia,serif;
  position: absolute;
  top: 6px;
  left: 8px;
}

body {
  color: #444;
  font-family: arial, sans-serif;
  font-size: 14px;
  background-image: none;
  background-color: #f2f2f2;
}


.entry-content p {
  font-size: 16px;
}

.entry-content pre {
  line-height: 1.4;
}

body.page-embed {
  background-color: transparent;
}

.page-embed .entry-title {
  text-align: inherit;
}
.page-embed .entry-title a {
  font-size: 18px;
}

footer iframe[src*="amazon-adsystem"],
footer div[id*="google_afc"] *,
footer span[id*="ninja"],
ins {
  display: none !important;
}

.editor-body-textarea {
  font-family: monospace;
}

input[type="submit"] {
  -webkit-appearance: none;
  display: block;
  width: 100%;
}
}
