css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("intelex.com") {
    
    #moduleHeader {
		display:none;
	}
    
    .apptabs {
		margin-top:40px;
	}
	
    
    .detpage .fieldarea-row {
        border-bottom: 0!important;
    }
    
    .detpage .fieldarea-caption .caption-wrapper {
        font-weight: normal!important;
        color: #FF72DB!important;
    }
    
    .secbase > .header > .main > .title-container .text {
        font-weight: normal!important;
        color: #FF72DB!important;
    }
    
    .detpage .panel-wrapper > .header > .title {
        font-weight: normal!important;
        color: #FF72DB!important;
    }
    
    .apptabs .tab-content {
        background:none!important;
        border:none!important;
    }
    
    .secbase > .header > .left {
        display:none;
    }
    
    .secbase > .header > .right {
        display:none;
    }
    
    .apptabs ul.header .extra-menu {
        background-image:none!important;
    }
    
    .detpage > * > .header .left {
       display:none;
    }
    
    .detpage > * > .header .right {
       display:none;
    }
    
    .secbase > .header > .main {
       margin:0!important; 
    }
    
    .secbase > .header {
        border-bottom: 1px solid #CCCCCC;
    }
    
    .secbase > .footer {
        display:none;
    }
    
    .secbase > .header > .main {
        border:none!important;
        background:none!important;
    }
    
    .secbase > .content {
        background:none!important;
        border:none!important;
    }
    
    .secbase {
        background-color:#F7F7F7!important;
        border: 1px solid #CCCCCC;
        border-radius: 4px 4px 4px 4px;
    }
    
    .detpage input, .detpage textarea, .detpage select {
        border-radius: 3px 3px 3px 3px;
        padding: 2px 0;
    }
    
    .detpage .panel-wrapper > .header { 
        border-bottom-color: #FF72DB!important;
    }
    
    .detpage .fieldarea-row {
        padding:3px 0;
    }
    
    body {
        background:none!important;
    }
    
    .detpage > * > .header .main {
        background:none!important;
    }
    .detpage > * > .header {
        background-color:lightyellow;
        border:1px solid #CCCCCC;
    }
    
    .detpage > * > .header .main .title-container .text {
        color:#FF72DB!important;
    }
    
    .l {
        display:none;
    }
    
    .r {
        display:none;
    }
    
    #moduleHeader {
		padding: 3px 20px;
    }
    
    .apptabs ul.header, .apptabs ul.header ul {
        padding: 0 17px!important;
    }
    
    .apptabs .tab-content {
        padding: 5px 20px!important;
    }
    
    .apptabs ul.header li a.selected span.m {
        background-image:none!important;
        background-color: #FF72DB!important;
        border-color: #C0C0C0!important;
        border-radius: 5px 5px 0 0;
        border-style: solid solid none!important;
        color: #FFFFFF!important;
        padding: 9px 20px!important;        
    }
    
    .apptabs ul.header li a span.m {
        background-image:none!important;
        background-color: #FFE0F7!important;
        border-color: #AED0EA!important;
        border-radius: 5px 5px 0 0;
        border-style: solid solid none!important;
        border-width: 1px!important;
        padding: 9px 20px!important;
        margin:0 3px!important;
    }
    
    #intelexFooterContainer {
        background:none!important;
    }
    
    .page-header {
        background-color: #FF72DB;
        color:#FFFFFF!important;
        font-size: 12px!important;
        padding:5px 10px!important;
        margin-bottom:18px!important;
        border-width:1px;
        border-color:#C0C0C0;
        border-style:none solid solid solid;
    }
    
    .page-header .left {
       font-size: 12px!important;
    }
    
    .page-header a {
        color:#FFFFFF!important;
    }
    
     .detpage .menu-top-container {
        height: 40px!important;
    }
    
    .detpage > * > .header-docked {
        height: 76px!important;
    }
    
    .detpage .menu-top-container .main {
        color:lightyellow!important;
    }
    
    .detpage .menu-top-container a {
        background-color: #F4B400;
        background-image: -moz-linear-gradient(center top , #F2C23E, #F4B400);
        border: 1px solid #F4A700;
        border-radius: 2px 2px 2px 2px;
        color: white!important;
        padding: 5px 10px;
        text-decoration: none;
        font-weight:normal;
    }
    

	.detpage .menu-top-container input {
	    background-color: #F4B400;
        background-image: -moz-linear-gradient(center top , #F2C23E, #F4B400);
		border-radius: 2px 2px 2px 2px;
		font-weight:normal!important;
		color: white!important;
		margin: 0 5px 0 0px!important;
		padding: 5px 10px!important;
	}
	
	.detpage .links a, .detpage .links .disabled_link {
	    background-image: -moz-linear-gradient(center top , #F2C23E, #F4B400)!important;
	    
	}
	
	.datagrid thead {
        background-color: #F6F6F6!important;
        color: #FF72DB!important;
    }
    
    .dropdown a {
        color: inherit!important;
    }
    
    .dropdown_text span{
        background-color: #F4B400;
        background-image: -moz-linear-gradient(center top , #F2C23E, #F4B400);
        border: 1px solid #F4A700;
        border-radius: 2px 2px 2px 2px;
        padding: 5px 10px;
        text-decoration: none;
    }
    
    .grid-update-actions-container {
        background-color:#F4B400;
    }
    
    .links_container {
        color:white;
    }
    
    .data_row td {
        border-left: 1px solid #C0C0C0;
        padding: 5px!important;
    }

    .datagrid .td_selector {
        border-left:none;
    }
    
    .td_edit_link {
        border-left:none!important;
        padding-right:5px!important;
    }
    
    .data_row.alternate {
        background-color:#FEFEFE!important;
    }
    
    .datagrid_footer td {
        background-image:none!important;
        background-color:#F6F6F6!important;
    }
    
    .datagrid {
        margin-top:20px;
    }
    
    .links {
        background:none!important;
        border:none!important;
    }
    
    .grid_container .footer-wrapper table {
        color: #FF72DB!important;
    }



	.flag-container a:link, .links_container a:link {
		background-color: #F4B400;
		background-image: -moz-linear-gradient(center top , #F2C23E, #F4B400);
		border: 1px solid #F4A700;
		border-radius: 2px 2px 2px 2px;
		color: white!important;
		padding: 5px 10px;
		text-decoration: none;
	}
    
    .links td a {
        color:#FFFFFF!important;
    }
    .active_list {
        background-color:#DDDDDD!important;
    }

}
