/*== С„РѕСЂРјР°
 *========================================*/
 form { width: 100%!important;}
/*== Form Elements
 *=================================*/
label,input,textarea,select,button,.btn {
	font:13px sans-serif;
	line-height:19px;
}
input,textarea,select,button,.btn {
	display:inline-block;
	padding:4px;
	margin:0;
	outline:none;
	background-color:#FFF;
	border:1px solid #D7D7D7;
	-webkit-border-radius:3px;
	border-radius:3px;
}
input[type="text"],
input[type="password"],
textarea {
	background-color:#F5F5F5;
	background-image:-khtml-gradient(linear,left top,left bottom,from(#F9F9F9),to(#F0F0F0));
	background-image:-moz-linear-gradient(#F9F9F9,#F0F0F0);
	background-image:-ms-linear-gradient(#F9F9F9,#F0F0F0);
	background-image:-webkit-gradient(linear,left top,left bottom,color-stop(0%,#F9F9F9),color-stop(100%,#F0F0F0));
	background-image:-webkit-linear-gradient(#F9F9F9,#F0F0F0);
	background-image:-o-linear-gradient(#F9F9F9,#F0F0F0);
	background-image:linear-gradient(#F9F9F9,#F0F0F0);
	-webkit-box-shadow:1px 1px 1px rgba(120,120,120,0.1) inset;
	box-shadow:1px 1px 1px rgba(120,120,120,0.1) inset;
	-webkit-transition:background .15s;
	-moz-transition:background .15s;
	-ms-transition:background .15s;
	-o-transition:background .15s;
	transition:background .15s;
}
input[type="text"],
input[type="password"] {
	height:29px!important;
}
textarea {
	overflow:auto
}
select {
	height:29px;
}
input[type="checkbox"],
input[type="radio"] {
	vertical-align:text-top;
	padding:0;
	margin:1px 0 0;
	border:0;
}
input[type="text"]:focus,
input[type="password"]:focus,
textarea:focus {
	outline:0;
	color:#333;
	border-color:#BBB;
	-webkit-box-shadow:inset 0 1px 30px rgba(0,0,0,.05);
	box-shadow:inset 0 1px 3px rgba(0,0,0,.05);
}

/*== Button Style
 *=================================*/
input[type="submit"]{
	cursor:pointer;
	cursor:hand;
}

input[type=submit],
input[type=button],
input[type=reset]{
	display:inline-block;
