css:
@-moz-document url("http://www.allmystery.de/"), 
url("https://www.allmystery.de/"), 
url-prefix("http://www.allmystery.de/themen"), 
url-prefix("https://www.allmystery.de/themen"),
url-prefix("http://www.allmystery.de/ena/"), 
url-prefix("https://www.allmystery.de/ena/"),
url-prefix("http://www.allmystery.de/artikel/"), 
url-prefix("https://www.allmystery.de/artikel/"),
url-prefix("http://www.allmystery.de/stats/"),
url-prefix("https://www.allmystery.de/stats/"), 
url-prefix("http://www.allmystery.de/chat/"), 
url-prefix("https://www.allmystery.de/chat/"), 
url-prefix("http://www.allmystery.de/wiki/"), 
url-prefix("https://www.allmystery.de/wiki/"),
url-prefix("http://www.allmystery.de/pn"), 
url-prefix("https://www.allmystery.de/pn"), 
url-prefix("http://www.allmystery.de/fcg"), 
url-prefix("https://www.allmystery.de/fcg"), 
url-prefix("http://www.allmystery.de/am"), 
url-prefix("https://www.allmystery.de/am"), url-prefix("https://www.allmystery.de/videos/") {

body.page, body.popup, .titelg {
    background-image:url("http://static.allmystery.de/upics/d59e62_2138393.jpg");
    background-color: #transparent;
    background-attachment:fixed;
    }

body {
    color: #ffffff;
    }

a {
    color: #ffffff;
    }

a:hover {
    color: #ffffff;
    background: rgba(20,20,20,1.0);
    border-radius: 4px 4px;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }
    
.header {
    background-image: url("http://static.allmystery.de/upics/30350a_header2.PNG");
    border-radius: 5px 5px;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

.tabnav a {
    background-color: rgba(20,20,20,1.0);
    color: #ffffff;
    border-radius: 4px 4px;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

.tabnav a:hover {
    background: transparent;
    border-radius: 4px 4px;
    }

#moreMenu {
    border: 1px solid transparent;
    background-color: rgba(20,20,20,1.0);
    color: #ffffff;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

.sufu {
    border: 1px solid transparent;
    border-radius: 4px 4px;
    }
  
.quick_item_header {
    background: rgba(20,20,20,1.0);
    border-radius: 4px 4px;
    }  
  
#se {
    background: rgba(20,20,20,1.0);
    border: 1px solid #454545;
    border-radius: 4px 4px;
    }  
  
.mcell {
    background-color: rgba(20,20,20,0.8);
    border: 1px solid transparent;
    border-radius: 6px 6px;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

.mcell_inhalt, .mcell_inhalt2, .mcell_inhalt3, .mcell2, .mcell_left {
    background-color: rgba(20,20,20,0.8);
    border: 1px solid transparent;
    border-radius: 6px 6px;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

A.at {
    color: #ffffff;
    }

a.at :hover, a.at_bold :hover {
    color: #ffffff;
    }

.at_in {
    color: #ffffff;
    }

.sub_info_p, .sub_info_p_check {
    color: #ffffff;
    }

.sub_info {
    color: #ffffff;
    }

.jump {
    color: #ffffff;
    }

table {
	border-spacing: 4px;
    }

div.categoryHeader {
    background-color: rgba(20,20,20,0.8);
    border: 1px solid transparent;
    border-radius: 4px 4px;
    }

.categoryHeader a:hover {
    color: #ffffff;
    background-color: #000000;
    border-radius: 4px 4px; 
    }

img.smallCategoryImage {
    border-color: transparent;
    border-radius: 5px 5px;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

#intro {
    background: rgba(20,20,20,0.8);
    border: 1px solid transparent;
    border-radius: 6px 6px;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

.paginator {
    background-color: rgba(20,20,20,0.8);
    border: 1px solid transparent;
    border-radius: 6px 6px;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

.post, .post:nth-child(even), .paginatorLast, .paginator {
    background-color: rgba(20,20,20,0.8);
    border: 1px solid transparent;
    border-radius: 6px 6px;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

.useravatar {
    border-radius: 5px 5px;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

.finfo {
    border-right: solid 1px transparent;
    }

.fbeitrag {
    border-left: solid 1px transparent;
    }

.paginator {
    border-bottom: 1px solid transparent;
    }

.fm td, .fh td, .fd td , .videos .videoitem, .videos .videoitem:nth-of-type(even){
    border-radius: 5px 5px;
    background-color: rgba(20,20,20,0.8);
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

tr.fm td {
    background-color: rgba(74,74,74,1.0);
    } 

textarea {
    border: 1px solid transparent;
    border-radius: 6px 6px;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }
    
input {
    border: 1px solid transparent;
    border-radius: 6px 6px;
    color: #ffffff;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

.rateBar {
    border:1px solid transparent;
    border-radius: 4px 4px; 
    }

.rateGraph {
    background-color: rgba(20,20,20,0.8);
    border: 1px solid transparent;
    border-radius: 6px 6px;
    box-shadow: 0px 0px 10px #ffffff;
    }

#rateContainer.mustVote .rateIt:hover .rateGraph, .rateIt.voted .rateGraph {
    background-color: #454545;
    border: 1px solid transparent;
    border-radius: 6px 6px;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

.header_info {
    background-color: rgba(20,20,20,0.8);
    border: 1px solid transparent;
    border-radius: 6px 6px;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

.status {
    background-color: transparent;
    border-radius: 5px 5px; 
    text-align: center;
    }

a img {
    border-color: #454545;
    border-radius: 5px 5px; 
    }

.message, .message_info {
    background-color: rgba(20,20,20,0.8);
    border: 1px solid transparent;
    border-radius: 6px 6px;
    color: #ffffff;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

div.thread_vote_container {
    background-color: #454545;
    }

span.thread_vote_container_sub {
    background-color: transparent;
    color: red;
    }

.blogs-prevpost.even {
    background-color: rgba(20,20,20,0.8);
    border: 1px solid transparent;
    border-radius: 6px 6px;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

.blogs-prevpost {
    background-color: rgba(20,20,20,0.8);
    border: 1px solid transparent;
    border-radius: 6px 6px;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

div.blogs-prevpost.even {
    margin-top: 6px;
    }
  
div.blogs-prevpost {
    margin-top: 6px;
    }

.voteup {
    background-color: rgba(20,20,20,0.8);
    border: 1px solid transparent;
    border-radius: 6px 6px;
    color: #ffffff;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

.voteup:hover {
    cursor: pointer;
    background-color: transparent;
    border-radius: 5px 5px; 
    box-shadow: 0px 0px 30px #ff0000;
    color: #ffffff;
    }

div[style="background:#4F4F64"] {
    background-color: #454545 !important;
    }

.in {
    background-color: rgba(20,20,20,0.8);
    border: 1px solid transparent;
    border-radius: 6px 6px;
    box-shadow: 0px 0px 20px rgba(74,74,74,1.0);
    }

a.mod {
    color: #ff0000;
    }

.footer hr {
    background-color: transparent;
    }

div.post.warn {
    background-color: rgba(152,0,0,0.8) !important;
    box-shadow:inset 50px 20px 80px #ff0000 !important;
    }

}
