css:
@-moz-document url-prefix("https://docs.google.com"), url-prefix("https://drive.google.com") {
#docs-chrome {
    background: #333;
}
#docs-toolbar-wrapper {
    background: #333;
    border-top: 1px solid rgb(0, 0, 0);

    border-bottom: 1px solid rgb(83, 83, 83);
}
#docs-toolbar {
    background: transparent;
}
#docs-editor-container {
    background: #111!important;
}
#docs-editor {
    background: #111!important;
}

#navpane {
    background: #111!important;
    color: #FFF!important;
}
.goog-list {
    background: transparent;
}
.nav-tree-folder-view .goog-list.density-tiny .goog-tree-row {
    
    border: 1px solid #000!important;
    
}
.goog-tree-item-label {

color: #888!important;
}
.nav-tree-folder-view .goog-tree-row-hover {
background: #333!important;
border: 1px solid #444 !important;
color: #666!important;
}
.goog-menu {
    background: rgba(0,0,0,0.8) !important;
  color:#FFF !important;
  box-shadow:#000 0 0 14px;
}
.goog-menu * {
    color:#777 !important;
  }
.navpane .navpane-top-list .goog-listitem {
    border: 1px solid #000!important;
}
.goog-listitem {
    color: #666!important;
}
.goog-list .goog-option-selected {
background: #333 !important;
font-weight: bold!important;
}
#gbx1, #gbx2 {
background: #111;
background: -webkit-gradient(radial,100 36,0,100 -40,120,from(#000),to(#333)),#555;
border-bottom: 1px solid #000;
border-color: #000;
}
#viewpane {
    background: #111;
}
.promo-banner {
background-color:rgba(0,0,0,0.5);
border-bottom: 1px solid #000;
color: #666;
}
.doclistview-inner, .doclistview-inner-ie {
background-color: rgba(0,0,0,0.5);
}
.doclist-header th {
background: #222;

}
.doclist-header th {
border-bottom: 1px solid #444;
}

.doclist-name {
    color:#888;
    border: 1px solid #000;
}

.doclist-tr {
    border-color: #444;
}
.doclist-tr-underlined .doclist-td, .doclist-tr-underlined .doclist-td-content, .doclist-tr-underlined .doclist-td-checkbox, .doclist-tr-underlined .doclist-td-date, .doclist-tr-underlined .doclist-td-modifiers, .doclist-tr-underlined .doclist-td-name, .doclist-tr-underlined .doclist-td-owners, .doclist-tr-underlined .doclist-td-quota, .doclist-tr-underlined .doclist-td-spacing, .doclist-tr-underlined .doclist-td-share-date, .doclist-tr-underlined .doclist-td-star, .doclist-tr-underlined .doclist-td-trash {
border-bottom: 1px solid rgb(49, 49, 49);
}

.gv-view-name {
color: #888;
}
.gv-dynamic-thumbnail {
    background: #333;
}
.gv-doc-selected .gv-image img {
border: 4px solid #777;
-webkit-box-shadow: 3px 3px 12px -1px #000;
-moz-box-shadow : 3px 3px 12px -1px #000;
box-shadow: 3px 3px 12px -1px #000;
}
.gv-image img {
border: 1px solid #000;
border-radius: 0px;
-webkit-box-shadow: 3px 3px 12px -1px #000;
-moz-box-shadow: 3px 3px 12px -1px #000;
box-shadow: 3px 3px 12px -1px #000;
}

.gv-doc-selected .gv-selection {
background-color: rgba(48, 48, 48, 0);
}
.gv-active .gv-selection {
border: 1px solid rgba(33, 34, 36, 0);
}
.gv-selection {
border: 1px solid transparent;
display: inline-block;
padding: 2px;
}
.goog-modalpopup-bg, .modal-dialog-bg {
background: rgb(0, 0, 0);
}

.modal-dialog {
color: #666;
}
.goog-modalpopup, .modal-dialog {
-webkit-box-shadow: 0 4px 16px rgba(0,0,0,.8);
-moz-box-shadow: 0 4px 16px rgba(0,0,0,.8);
box-shadow: 0 4px 16px rgba(0,0,0,.8);
background: #222;
border: 1px solid #333;
}
  .modal-dialog-content {
background-color: rgba(7, 7, 7, 0);
}
.permissions-list-container {
border-top: 1px solid rgb(0, 0, 0);
}
.permissions-list td {
border-bottom: 1px solid rgb(0, 0, 0);
}
.inviter-invite-area {
background-color: rgba(245, 245, 245, 0.05);
}
.modal-dialog {
-webkit-box-shadow: 0 4px 16px rgba(0,0,0,.8);
-moz-box-shadow: 0 4px 16px rgba(0,0,0,.8);
box-shadow: 0 4px 16px rgba(0,0,0,.8);
background: rgb(34, 34, 34);
border: 1px solid transparent;
}
  .modal-dialog-title {
background-color: rgba(255, 255, 255, 0);
color: rgb(255, 255, 255);
cursor: default;
font-size: 16px;
font-weight: bolder !important;
}
}
