css:
/* 

BTC-e.com Elastic Style v1.1
author: goahead from btc-e.com 

*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("btc-e.com") {

#body {
    width: 100% !important;
}

#header {
    float: right !important;
}

#orders-self-list {
    position: absolute;
    left: 4px !important;
    top: 0;
}

#orders-self-list table {
    background-color: white;
    border-collapse: collapse;
}

#orders-self-list table, #orders-self-list td {
    border: 1px solid gray;
}

#orders-self-list table tbody .table_h td {
    font-weight: bold;
    padding: 0 4px 0 0 !important;
}

#orders-self-list table tbody tr td {
    padding: 0 4px 0 2px !important;
}

#content {
    width: 100% !important;
}

div#content > div {
    width: 50% !important;
    margin: 5px;
    padding: 5px;
}

div#content div > div.block {
    width: 97% !important; 
}

div#content div div.block table.tabla {
/*    display: none !important; */
}

div#content div div.block div.inblock div.inblock {
    width: 95% !important;
}

div#content div div.block div.inblock:not([id]) {
    width: 47% !important;
}

#nChatCon, #nChat {
    width: 100% !important;
}

#nChatCon {
    height: 750px !important;
}

#nChat {
    height: 95% !important;
    width: 95% ;
}

#nChatInput {
    width: 95% !important;
}

#nChatSmilesButton {
/*    width: 100% !important; */
/*   clear: right !important; */
    margin-left: 47% !important;
}

#nChatSmiles {
    margin-left: 36% !important;
}

#trade_history {
    width: 100% !important;
}

}
