css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("delicious.com") {

.page-header-inner.container {
    width: 95%!important;
    }
    
.global-search {
    min-width: 400px!important;
    }
    
#search-field {
    width: 100%!important;
    }

#delicious.container {
   width: 95%!important;
   }

section.span2 {
   float: right!important;
   width: 15%!important;
   padding-left: 10px;
   }

.profile-avatar, .avatar overlay {
    width: 100px!important;
    height: 100px!important;
    }
    
section.span6 {
    width: 80%!important;
    }

.profile-header h1 {
    font-size: 16px!important;
    }

.profile-bio {
    display: none!important;
    }

#bulk-edit-actions.hidden-tablet {
    display: inline!important;
    }

#bulk-edit-dd.btn-group.btn-container, #bulk-edit-btn {
    margin-right: 5px!important;
    margin-top: -48px!important;
    margin-bottom: 5px!important;
    }
    
section.span6 > .links-container > .links > .link-list > .link.animated-item-view {
    width: 100%!important;
    }
   
.link-details.span4.hidden-intro {
   display: none!important;
   }



}
