css:
@-moz-document url-prefix("http://vocadb.net"), domain("vocadb.net") {
body {
	background: url(http://static.vocadb.net/img/miku3.jpg)top center no-repeat fixed;
	background-size: 100%;
}

body h3 small {
	color: #474747;
}

body h2 small {
	color: #474747;
}

a {
	color: rgb(226, 74, 190);
	text-decoration: none;
}
  
a:hover {
    color: rgb(255, 52, 141)
    }

a:visited {
	color: rgb(213, 0, 0)
	}



.btn-info {
	color: rgb(255, 255, 255)!important;  
}

  
  
.btn-inverse, .btn-inverse:hover,.btn-inverse:focus,.btn-inverse:active,.btn-inverse.active,.btn-inverse.disabled,.btn-inverse[disabled]  {
	color: rgb(255, 255, 255)!important;
}

.btn-inverse {
	color: #fff!important;
	text-shadow: 0 -1px 0 rgba(0,0,0,.25);
	background-color: rgb(204, 0, 0);
	background-image: -ms-linear-gradient(top,rgb(255, 153, 231),rgb(204, 0, 0));
	background-image: -webkit-gradient(top,0 0,0 100%,from(rgb(255, 153, 231)),to(rgb(255, 0, 163)));
	background-image: -webkit-linear-gradient(top,rgb(255, 153, 231),rgb(255, 0, 163));
	background-image: -o-linear-gradient(top,rgb(255, 153, 231),rgb(255, 0, 163));
	background-image: -moz-linear-gradient(top,rgb(255, 153, 231),rgb(255, 0, 163));
	background-image: linear-gradient(top,rgb(255, 153, 231),rgb(255, 0, 163));
	background-repeat: repeat-x;
	border-color: rgba(255, 144, 242, 0.89) rgba(255, 144, 242, 0.7) rgba(255, 144, 242, 0.55);
}

.btn-inverse:hover {
    background-color: rgb(255, 0, 163); 
    -webkit-animation-name: pinkPulse;
	-webkit-animation-duration: 1s;
	-webkit-animation-iteration-count: infinite;
}
    
.btn-inverse:active {
    -webkit-animation-name: pinkPulse-2;
	-webkit-animation-duration: 1s;
	-webkit-animation-iteration-count: infinite;
}

.scrollable-navi {
	padding: 0!important;
	margin: 0!important;
	margin-left: -45px;
	border-bottom-left-radius: 10px;
	background-color: rgba(233, 137, 170, 0.38);
	border-top-left-radius: 10px;
}

.scrollable-item.active {
	background-color: rgb(233, 137, 170);
}

.scrollable-item:hover {
	background-color: rgba(233, 146, 177, 0.85);
}

.scrollable-item {
	background-color: rgba(233, 146, 177, 0.45);
	border-top-color: rgb(244, 209, 221);
	border-top: 1px solid rgb(216, 138, 200);
	clear: both;
	color: rgb(48, 48, 48);
	font-size: 12px;
	height: 81x;
	list-style-type: none;
	list-style: none;
	width: 190px;
	cursor: pointer;
	margin-top: 0!important;
	border-top-color: rgb(244, 209, 221);
}

.scrollable-vertical {
	border-bottom: 1px solid rgb(244, 209, 221);
	border-top: 1px solid rgb(244,209,221);
}

.pvViewerContent {
	position: relative;
	overflow: hidden;
	height: 462px;
	min-width: 555px;
	max-width: 560px;
	padding: 10px;
	border-bottom-right-radius: 10px;
	border-top-right-radius: 10px;
	background-color: rgba(233, 137, 170, 0.38);
}

      
.pvViewerContent .songInfoButton {
	color: #fff!important;
    float: left;
	margin-left: 225px;
	background-color: rgb(255, 0, 163);
	background-image: -ms-linear-gradient(top,rgb(255, 153, 231),rgb(204, 0, 0));
	background-image: -webkit-gradient(top,0 0,0 100%,from(rgb(255, 153, 231)),to(rgb(255, 0, 163)));
	background-image: -webkit-linear-gradient(top,rgb(255, 153, 231),rgb(255, 0, 163));
	background-image: -o-linear-gradient(top,rgb(255, 153, 231),rgb(255, 0, 163));
	background-image: -moz-linear-gradient(top,rgb(255, 153, 231),rgb(255, 0, 163));
	background-image: linear-gradient(top,rgb(255, 153, 231),rgb(255, 0, 163));
    background-repeat: repeat-x;
}

.pvViewerContent .songInfoButton:focus {
	color: #fff;
    -webkit-animation-name: pinkPulse-2;
	-webkit-animation-duration: 1s;
	-webkit-animation-iteration-count: infinite;
      }

.pvViewerContent .songInfoButton:hover {
    color: #fff!important;
  	background-color: rgb(255, 0, 163);
    -webkit-animation-name: pinkPulse;
	-webkit-animation-duration: 1s;
	-webkit-animation-iteration-count: infinite;
  	background-position: 0 -30px;
}
     
.pvViewer-title a {
  color:#8A0037!important;
}

.pvViewer-translatedNames {
	margin-left: 5px;
	color: rgb(0, 0, 0);
	font-size: smaller;
}

.pvViewer-artists {
	color: #474747;
}



.menu>.well {
	background-color: #cdecf4;
	background-color: rgba(244, 205, 222, 0.85);
	border: solid rgb(216, 138, 200) 2px;
	float: left;
	vertical-align: top;
    width: 200px;
}

.menu a {
	text-decoration: none;
	color: rgb(226, 74, 190);
}

.menu a:hover {
	color: rgb(219, 0, 193)!important;
	text-decoration: underline;
}

.menu a:visited {
	color: rgb(90, 22, 85);
} 

     
    



.rightFrame {
	background-color: #e8faff;
	background-color: rgba(244, 205, 222, 0.85);
	border: solid rgb(216, 138, 200) 2px;
}

.rightFrame a {
  	color: rgb(184, 0, 140)
}

.rightFrame a:visited {
	color: rgb(240, 76, 141)
}

.rightFrame a:hover {
	color: rgb(255, 111, 175)
  }

.rightFrame .ui-widget-header a, .rightFrame .ui-widget-content a, .rightFrame .ui-dialog.ui-widget-content a {
	color: rgb(184, 0, 140)
}

.rightFrame .ui-widget-header a:hover, .rightFrame .ui-widget-content a:hover, .rightFrame .ui-dialog.ui-widget-content a:hover {
	color: rgb(255, 136, 183)
}

.rightFrame .ui-widget-header a:visited, .rightFrame .ui-widget-content a:visited, .rightFrame .ui-dialog.ui-widget-content a:visited {
	color: rgb(240, 76, 141)
}

.ui-widget-header {
	border: 1px solid rgb(204, 92, 182);
	background: rgb(204, 92, 182) url(http://i.imgur.com/KuQ444J.png) 50% 50% repeat-x;
	color: #fff;
}

.ui-widget-content {
	border: 1px solid rgb(223, 24, 151);
}

.alert-info {
	color: rgb(135, 45, 85);
	background-color: rgb(247, 217, 217);
	border-color: rgb(241, 188, 188);
}

.btn-primary:hover, .btn-primary:active, .btn-primary:focus, .btn-primary.active, .btn-primary.disabled, .btn-primary[disabled] {
	color: #fff;
	background-color: rgb(255, 0, 163)!important;
  	background-position: 0 -30px;
	-webkit-animation-name: pinkPulse;
	-webkit-animation-duration: 1s;
	-webkit-animation-iteration-count: infinite;
    }
    
.btn-primary:active {
	-webkit-animation-name: pinkPulse-2!important;
	-webkit-animation-duration: 1s;
	-webkit-animation-iteration-count: infinite;
}
    
.btn-primary {
	background-color: rgb(204, 0, 0);
	background-image: -ms-linear-gradient(top,rgb(255, 153, 231),rgb(204, 0, 0))!important;
	background-image: -webkit-gradient(top,0 0,0 100%,from(rgb(255, 153, 231)),to(rgb(255, 0, 163)));
	background-image: -webkit-linear-gradient(top,rgb(255, 153, 231),rgb(255, 0, 163))!important;
	background-image: -o-linear-gradient(top,rgb(255, 153, 231),rgb(255, 0, 163))!important;
	background-image: -moz-linear-gradient(top,rgb(255, 153, 231),rgb(255, 0, 163))!important;
	background-image: linear-gradient(top,rgb(255, 153, 231),rgb(255, 0, 163))!important;
	background-repeat: repeat-x;
}


ul.nav.nav-pills li.active a {
	color: #fff;
	background-color: rgb(240, 76, 141);
    }
    
    
    
@-webkit-keyframes pinkPulse { 
	from {background-image: #FF3366;-webkit-box-shadow: 0 0 9px #FF3366; }
	50%{background-image: #FF99E7;-webkit-box-shadow: 0 0 24px #FF99E7; }
	to{background-image: #FF00A3;-webkit-box-shadow: 0 0 9px #FF00A3; }
}


@-webkit-keyframes pinkPulse-2 { 
	from {background-color: #FF3366;-webkit-box-shadow: 0 0 9px #FF3366; }
	50%{background-color: #FF99E7;-webkit-box-shadow: 0 0 24px #FF99E7; }
	to{background-color: #FF00A3;-webkit-box-shadow: 0 0 9px #FF00A3; }
}
}
