css:
@-moz-document url-prefix("http://egza.org/bird/") {

#tw.tw.tw-parent {
    top:72px !important;
    }
#misc {
    margin-right:400px !important;
}
#menu a.new {
background-color:rgba(7, 173, 255, 0.8);

background-image: -moz-radial-gradient(center bottom, ellipse farthest-side, rgba(7, 173, 255, 0.8), #333333 100%);

background-image: -webkit-radial-gradient(center bottom, ellipse farthest-side, rgba(7, 173, 255, 0.8), #333333 100%);

background-image: -o-radial-gradient(center bottom, ellipse farthest-side, rgba(7, 173, 255, 0.8), #333333 100%);

background-image: -ms-radial-gradient(center bottom, ellipse farthest-side, rgba(7, 173, 255, 0.8), #333333 100%);

background-image: radial-gradient(center bottom, ellipse farthest-side, rgba(7, 173, 255, 0.8), #333333 100%)
}

body {
     padding:0px !important;
    margin:0px !important;
 }
#counter-div {
    top:17px !important;

}
#counter-p1  {
    visibility:hidden !important;
    
}
#counter-p2  {
    visibility:hidden !important;
}
#counter {
    font-family:Tahoma !important;
	font-style:normal !important; 
	padding:0px !important;
	font-size:30pt !important;
	color:#ccc !important;
	background-color:none !important;
	width:0px !important;
	}
#menu {
	background-color: #333  !important;
	top:0px  !important;
	position:relative  !important;
	padding:0px  !important;
	width: 100%  !important;
	text-align:center  !important;
  height:25px !important ;
}

#fst {
    font-size:11pt !important;
	top:30px  !important;
	height:40px  !important;
	width: 100% !important;
	margin:0px !important;
	padding: 7px 0px 0px 0px !important;
	
	padding-right:20px  !important;

}

#geotag {
	visibility: hidden  !important;
}

#option {
	visibility: hidden  !important;
	position: realative  !important;
}

#tw > div {
	background-color: #F2FCFF !important;
	font-family: tahoma !important;
	border:0 !important; 
}
#tw div:first-child > div {
top:-20px !important;
background-color:#FDFFEB !important;
}

.uid , .uicon {
  display:none !important ;
}

.tw-parent > div > div {
padding: 4px 4px 3px 2px !important ;
width:32% !important;
float:right !important;
border: 1px solid #bbb !important;
margin:1.2px !important;
background-color:#fff !important;

}

span.uid ,#tw a{
font-weight:bold !important;
text-decoration:none !important;
color:#999 !important;
}

.utils {
  font-size: 11px !important;
position:relative !important;
float:left !important;
text-align:center !important;
}

.utils a {
  
color:#dedede !important;
}


#control {
  
   height: 60px  !important;
   background-color:#000  !important;
}
#menu a {
    display: inline-block !important;
    font-size: 12px !important;
    height: 30px !important;
    line-height: 30px !important;
    border: 0px none !important;
    text-decoration: none !important;
    -moz-transition:all 0.5s !important;
    transition:all 0.5s !important;
}
.popup_menu a:hover {
background-color:#69f !important;}

}
