css:
body {
	background-color:#dbdbdb !important;
}

.wrapper.black {
  background-image:none !important;
    }

.topbar {
	width:300px;
}

.global-nav {
	width:300px !important;
}

.global-nav .container {
	width:300px !important;
}

.global-nav, .global-nav .well, .nav .active>a, .btn-tweet, .btn-tweet:hover, .btn-tweet:active, .nav .active>a, .card-soccer .cover {
	background-image:none !important;
  background-color:#202020;
}

.global-nav, .global-nav-outer {
	height:100%;
}

.topbar {
	height:100%;
}

.nav li {
width:100px;
  height:50px !important;
  margin-left:90px;
 
}

.content-main, .content-narrow, .page-canvas, .permalink {
-webkit-border-radius: 0px !important;
-moz-border-radius: 0px !important;
border-radius: 0px !important;
}

.dashboard {
display:none !important;
}


.nav .active a {
color: #fff;
text-shadow: none !important;
background-color: transparent;
-webkit-box-shadow: none !important;
-moz-box-shadow: none !important;
  box-shadow:none !important; }


.bird-topbar-etched, .bird-topbar-blue, .bird-topbar-white {
margin-bottom:35px;
}


.global-nav .form-search {
position:absolute !important;
  left:450px;
  top:0px;
}

.global-nav .form-search .search-input {
width:300px;
  background-image:none !important;
  background-color:transparent;
}

.account, .interest-category, .list, .saved-search, .tweet, .app, .discover-item {
position: relative;
min-height: 51px;
padding: 19px 12px;
border-bottom: 1px solid #e8e8e8;
  line-height: 20px; }

.global-nav .search-icon {
position: absolute;
top: 0;
left: -131px;
display: block;
width: 26px;
height: 26px;
cursor: pointer;
}

.global-nav .search-input {
display: block;
width: 163px;
height: 14px;
padding: 6px 27px 6px 12px;
font-size: 12px;
line-height: 1;
color: #666;
background-color: #ccc;
border: 0;
-webkit-border-radius: 13px;
-moz-border-radius: 13px;
border-radius: 13px;
-webkit-box-shadow: none;
-moz-box-shadow: none;
box-shadow: none;
-webkit-transition: all .3s ease-in-out;
-moz-transition: all .3s ease-in-out;
transition: all .3s ease-in-out;
  text-indent:20px;
}

.topbar-tweet-btn {
float: left;
margin-right: 13px;
position: absolute;
top: 0px;
right: -900px;
}

.topbar-divider {
display:none;}

.global-nav .pull-right .dropdown-toggle {
display:none;
}

.content-main {
width:600px;
}

.bird-topbar-etched {
margin-left:-8px;
}

.stream-item-header .avatar, .permalink-header .avatar {
top:22px;
}
