css:
@-moz-document domain("kotaku.com") {
.top-bar {
	background: rgba(180,0,92,0.95) !important;
	}
  
.header-follow,.top-bar.user-bar,ul.top-bar-tools>li>a,.icon {
    color: rgb(228, 193, 206) !important;
    box-shadow: 0 0 0px rgb(180,0,92) !important;
    border-right: 0px 0px 0px 0px dotted rgb(229,177,203);
	}

.replybranch>header {
	background: rgb(201, 146, 177) !important;
    border-bottom: 3px dotted rgb(231, 236, 115) !important;
  	}
  
.icon {
    opacity:1.0 !important;
	}

.button-flat {
    background-color:#fafafa !important;
    opacity: 1.0 !important;
    }
 
.text-light {
    color:#707070 !important;
  	opacity:0.6 !important;
    }

.button {
    color:#272727 !important;
    }
  
a:focus,a:hover,.kinjacolor:hover,.link:focus,.link:hover {
    color:#E272A2 !important;
    }

.notranslate,.no-text {
    opacity:1.0;
    }
    
.tag {
	color:#b22460 !important;
	}

.fb-like {
	display:none !important;
	}

}
