css:
@-moz-document
url-prefix("http://wapp.baidu.com"){

/* top space */
.j_tab_index_head{
    display:none;
}

/* forum tile */
.forumTile{
    padding:2px 3px!important;
    width:auto!important;
    list-style:none!important;
}

/* tieba top ads */
.common_forum{
    display:none;
}

/* author and reply  */
.post_list_item_info{
    margin-bottom:-8px!important;
}
.post_item_info_time{
    margin-bottom:-4px!important;
}
.post_item_info_author{
    margin-bottom:-4px!important;
}

/* prev and next page */
#list_pager{
    width:100%!important;
    display:block!important;
    height:40px!important;
}
#list_pager > a {
    color:#ccc!important;
    float:left!important;
    padding:3px 10px!important;
}
#list_pager > a > input {
    width:100%!important;
    color:#666!important;
    margin-top:6px!important;
}

/* input post */
.editor_input{
    width:98%!important;
}

/* smile */
.media_bar{
    margin-right:5px!important;
}
.smile_enable{
    padding:17px!important;
    background-position:-14px -14px !important;
}
.upload-block{
    background-position:-11px -130px!important;
    margin-left:5px!important;
}
.upload-input{
    width:40px!important;
}

}
