css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wykop.pl") {




a[href|='http://tablica.pl'], .add-full, li.sponsoredby, .ad-box, #fixedBox,
#wbk-comments-add, #wbk-flash-logo, #heyah_mgm_logotakeover_wykop, .heyah, .adsgl,
a.pluscount>:first-child, .boxjaramsie, .sponsoredby  {
    display: none;
}

.click {
    display: none;
    width: 0px !important;
    height: 0px !important;
}

.toggle-comment {
    right: 90px !important;
}

header nav { /* mniejsza belka */
    margin-top: 0px !important;
}
#header-con { /* mniejsza belka */
    min-height: 38px !important;
}
#body-con { /* mniejsza belka */
    margin-top: 18px !important;
}
#scrolling { /* mniejsza belka */
    display: none;
}
.newtag {
    box-shadow: none !important;
    background: #F0F0F0 !important;
    margin: 0 !important;
}
.observed_channels { /* wymagany osobny dodatek, kiedys opublikuje */
    -moz-columns: 2;
    columns: 2;
    -webkit-columns: 2;
    -o-columns: 2;
}
.observed_channels a {
    display: block;
    max-width: 140px;
}
.buttonnumber.dont span, .buttonnumber.dont {
    margin: 0 !important;
    padding: 3px !important;
}
ul.badges li, ul.badges li a { /* skompresowane odznaczenia */
    margin: 0 !important;
}


article.entry .imagebig, a .lazy { /* mniejsze miniaturkiprzy znaleziskach */
    width: 100px !important;
    height: 65px !important;
}
a[href*="/ludzie/stekelenburg2/"] strong, a[href*="/ludzie/stekelenburg2/"] {
    color: #339933 !important;
}
a[href*="/ludzie/Elfik32/"] strong {
    color: #000000 !important;
}		
nav a[href*="/tracker/"] {
    display: none !important;
}						
#reptile_reports_list ul {
    display: block;
    margin-left: 0;
    text-align: left;
}
#reptile_reports_list ul li span.date {
    float: right;
    font-size: 0.7em !important;
}
#reptile_reports_list ul li span.reason {
    display: block;
}
#reptile_reports_list ul li span.reporter {
    display: block;
    font-size: 0.9em !important;
}
#reptile_reports_list ul li {
    display: block;
}
#reptile_reports_list ul li a.reported_link {
    display: block;
    color: #367AA9 !important;
}
.removedItem {
    opacity: 1 !important;
}
.hidden div img {
    display: none;
}               	
.subcomments li {
    margin-bottom: 0 !important;
}
#header-con { /* to i nastepna regule mozna wywalic, po prostu tak wole */
    background-color: black !important;
    background: url(
    'http://i.imgur.com/G6bF5d4.png') !important;
    background-position: 0px 0px !important;
}
nav.main a.selected {
    color: black !important;
    border-color: transparent !important;
}
.ac_results {
    border-radius: 5px;
}
.ac_over {
    background-color: #36A97A !important;
}
article.entry div iframe.fbframe {
    display: none;
}
div.commercial-text, div.commercial {
    display: none !important;
    background-color: red;
}
.relatedContainer {
    height: auto !important;
}
#links-list article.entry {
    margin: 0 !important;
    padding: 0 !important;
    min-height: auto !important;
}
.prawak {
background: url(
"http://upload.wikimedia.org/wikipedia/commons/thumb/0/0b/Icone1.svg/14px-Icone1.svg.png")no-repeat scroll 0% 0% transparent;
padding-left: 15px !important;
}
.idiota {
background: url('http://upload.wikimedia.org/wikipedia/commons/thumb/1/1b/Gnome-face-monkey.svg/16px-Gnome-face-monkey.svg.png') no-repeat right;
padding-right: 17px !important;
}
.prawak.idiota{
background-image: url(
"http://upload.wikimedia.org/wikipedia/commons/thumb/0/0b/Icone1.svg/14px-Icone1.svg.png"), url('http://upload.wikimedia.org/wikipedia/commons/thumb/1/1b/Gnome-face-monkey.svg/16px-Gnome-face-monkey.svg.png');
background-position: left, right;
}

}
