css:
@-moz-document domain("chat.deviantart.com") {

#bar-deviant a{ color:#FFD800 !important; }
#bar-deviant{ color:#000000 !important; background-color: #B8860B !important; border: 0px !important; }
.alt0{ background-color: #B8860B !important; } 
.alt1{ background-color: #FF7E00 !important; } 
.aside-left, .damncr .userinfo .avatar{ background-color: #000000 !important; border: 0px !important; } 
.damn-lo-row-top{ background-color: #000000 !important; border: 0px !important; } 
.damncr-infoctr { color: #000000 !important; border: 0px !important; border: 0px !important; } 
.damncr-infoctr a:hover{ color: #B8860B !important; } 
.damncr-infoctr a{ color: #000000 !important; } 
.damncr-title a:hover{ color: #F4CA16 !important; } 
.damncr-title a{ color: #FBEC5D !important; } 
.damncr-title{ background-color: #F4CA16 !important; color: #FBEC5D !important; border: 0px !important; } 
.damncrc-chat .join, .damncrc-chat .part{ color: #FBEC5D !important; } 
.damncrc-chat .other-hl{ background-color: #3D2B1F !important; } 
.damncrc-chat .self-hl{ background-color: #3D2B1F !important; } 
.damncrc-chat .userinfo-inner .avatar, .damncrc-chat .userinfo-inner .bodyarea{ background: none !important; background-color: #50C878 !important; border: 1px !important; } 
.damncrc-chat .userinfo-inner div.read a:hover{ color: #FBEC5D !important; } 
.damncrc-chat .userinfo-inner div.read a{ color: #B8860B!important; } 
.damncrc-chat .userinfo-inner div.read{ color: #B8860B !important; background-color: #3D2B1F !important; border: 0px !important; } 
.damncrc-chat a:hover{ color: #FF7E00 !important; } 
.damncrc-chat a{ color: #FF7E00 !important; } .damncrc-chat span{ background: transparent !important; color:#F4CA16 !important; border: 0px !important; } .damncrc-chat{ background: #000000 !important; color: #000000 !important; border: 0px !important; } 
.damncrc-chat-window { border:none !important; } 
.damncrc-error{ background-color: #3D2B1F!important; color: #000000 !important; border: 0px !important; } 
.damncrc-topic a:hover{ color: #FDEE00 !important; } 
.damncrc-topic a{ color: #000000 !important; } 
.damncrc-topic{ background-color: #FDEE00 !important; color: #000000 !important; border: 0px !important; } 
.main span { color:#B8860B !important; border: 0px !important; } 
.sidebar dl.simple dt { color:#FF7E00 !important; border: 0px !important; } 
.tabbar a{ background-color: #000000 !important; border: 0px !important; } 
.tabbar a[style*=\"black\"]{ color: ##000000 !important; background-color: #000000 !important; border: 0px !important; } 
.tabbar a[style*=\"Amber"]{ background-color: #000000 !important; border: 0px !important; } 
.tabbar strong{ background: ##000000 !important; border: 0px !important; } 
div#body { background-color: #000000 !important; border: 0px !important; } 
dl.simple dd, dl.flat dd { line-height:1.2em !important; color:#000000 !important; border: 0px !important; } 
input.button:hover{ color: #FF7E00 !important; } 
input.button{ -moz-border-radius: 0 !important; background-color: #B8860B !important; border: 0px !important; color: #3D2B1F !important; font-weight: bold !important; font-size: 10px !important; text-transform: uppercase !important; padding: 3px !important; margin: 0px !important; }

}
