css:
@-moz-document domain(boards.4chan.org)
{
    *
    {
        font-weight: lighter !important;
    }
    #boardNavDesktop, .boardSelect strong, .boardBanner img,
    .boardSubtitle, hr{display:none !important;}
    a{color: #f7f7f7 !important;}
    body
    {
        background: #212121 !important;
        color: #777 !important;
    }
    #boardNavMobile
    {
        background: rgba(0, 0, 0, .7) !important;
        box-shadow: 0 0 14px #000 !important;
        border: none !important;
    }
    .pageJump a{padding:0 7px !important;}
    .boardTitle{color:#989898 !important;}
    .mobileib.button, #mpostform
    {
        background: #282828 !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0 0 7px #141414 !important;
    }
}

@-moz-document url-prefix(http://boards.4chan.org/vg)
{
    #postForm > tbody > tr:first-child, tr.mobile, #recaptcha_table >tbody > tr:first-child >
    td:nth-of-type(2), .passNotice{display:none !important;}
    #postForm tbody tr:nth-child(3) td{visibility:hidden !important;}
    #postForm tbody tr td:first-child
    {
        background: none !important;
        border: none !important;
        text-align: right !important;
        color: #777 !important;
        padding: 0 4px 0 0 !important;
    }
    #postForm tbody tr td input:not([type='button']):not([type='submit']):not([name='email'])
    {
        background: #141414 !important;
        border: none !important;
        -moz-appearance: none !important;
        color: #777 !important;
        width: 210px !important;
        height: 21px !important;
        margin: 0 !important;
        padding: 0 !important;
        font-size: 14px !important;
    }
    [name='email']
    {
        background: #141414 !important;
        border: none !important;
        -moz-appearance: none !important;
        color: #777 !important;
        height: 21px !important;
        margin: 0 !important;
        padding: 0 !important;
        font-size: 14px !important;
        width: 147px !important;
    }
    textarea
    {
        background: #141414 !important;
        border: none !important;
        -moz-appearance: none !important;
        margin: -23px 0 0 0 !important;
        padding: 0 !important;
        font-size: 14px !important;
        width: 210px !important;
    }
    input[value='Submit']
    {
        width: 70px !important;
        background: #141414 !important;
        -moz-appearance: none !important;
        border: none !important;
        color: #f7f7f7 !important;
        height: 21px !important;
        padding: 0 !important;
        font-size: 14px !important;
        visibility: visible !important;
        position: relative !important;
        top: -22px !important;
        left: -70px !important;
    }
    #captchaFormPart{display:table !important;}
    #captchaFormPart td:nth-child(2), .recaptcha_image_cell, .recaptcha_image_cell 
    center{height:auto !important;}
    #recaptcha_area, #recaptcha_widget_div, #captchaFormPart > td:nth-child(2) >
    div:not(.passNotice), #captchaFormPart > td:nth-child(2){display:table !important;}
    .recaptcha_image_cell, #captchaFormPart td:nth-child(2){width:auto !important;}
    #recaptcha_image
    {
        border: none !important;
        display: table !important;
        margin: 0 !important;
    }
    #recaptcha_image img
    {
        border: none !important;
        width: 210px !important;
        height: auto !important;
    }
}
