css:
@-moz-document url-prefix(https://crm.persimmonhomes.com) {

body {}

/*General Font & Spacing Fixes */

h1, h2{font-size: 14px!important; border-bottom:1px dotted #666; width:100%!important; display:block!important; margin:0!important; padding:0!important}

#wrapper{padding:0 24px!important}
div[style]{margin:0!important}

.tasksBox{padding:0 25px!important; margin:0!important}

/*Casual Button*/
#casual{position:fixed!important; top:10px!important; right:10px!important}

/*TABS*/
.tabs a{height:25px!important; line-height:25px!important}

#container{padding:0 25px!important}

.squiloople-pagination-limit, .squiloople-pagination-information{margin: 0!important}
.buttons.right{margin:0!important}

.selected {background:#dea!important}

/* FIX PROSPECT BUTTON*/
#container{position:relative!important}
#add{position:absolute!important; top:30px!important; left:538px!important}

/*SEARCH FIELDS*/
fieldset label {margin:0!important}
input{margin:0!important}

/*BUTTON FIX*/
.buttons input:hover, .bigButton:hover{background:#ffb!important; color:#333!important}

.datepicker a{cursor:pointer}

#notes{background-image:none!important; background:white!important;padding: 7px 12px 12px 10px!important;
    width: 261px!important; overflow-y:auto!important}

.lead_status{margin:0!important}

}
