css:
@-moz-document domain("reddit.com") {
@font-face {
  font-family: 'Noto Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Noto Sans'), local('NotoSans'), url(http://themes.googleusercontent.com/static/fonts/notosans/v1/LeFlHvsZjXu2c3ZRgBq9nD8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}
@font-face {
  font-family: 'Original Surfer';
  font-style: normal;
  font-weight: 400;
  src: local('Original Surfer'), local('OriginalSurfer-Regular'), url(http://themes.googleusercontent.com/static/fonts/originalsurfer/v2/gdHw6HpSIN4D6Xt7pi1-qKdZ-uyMVouHrkdj1hRNutY.woff) format('woff');
}

body { background: #121211; color: #888; }
::selection { background: #cee3f8; }
#sr-header-area { background: #111; color: #aaa; border-bottom: 1px solid rgba(206,227,248,0.5); }
.sr-bar a, .dropdown.srdrop .selected { color: #aaa; }
#sr-more-link { background: #444 !important; color: #ccc; border: 1px solid #222; }
#header { background: #2b2b2b; border-bottom: 4px solid rgba(206,227,248,0.3); }
#header-img { background: url(http://png-5.findicons.com/files/icons/2370/kadom_clean_social_icons/256/reddit.png) !important; background-size:42px 42px !important; background-position: 8px 5px !important; background-repeat: no-repeat !important; height: 48px !important; font-family: 'Original Surfer', cursive; font-size: 18pt; text-indent: 0 !important; padding: 8px 0 0 60px; color: #cee3f8 !important; height: 42px !important; }
#siteTable { margin-top: 30px; }

.thing { position: relative; margin: 25px auto; overflow: auto; border: 2px solid rgba(206,227,248,0.42); border-radius: 15px; padding: 8px; background: rgba(206,227,248,0.1) url(http://cdn1.iconfinder.com/data/icons/black-icon-social-media/512/099350-reddit-logo.png) !important; background-size:90px 90px !important; background-position: 5px -12px !important; background-repeat: no-repeat !important; min-height: 50px; max-width: 960px; }
/* .thing { background: #1c1a1a; border: 2px solid #444442; } */
.thing:hover > .midcol { display: block !important; float: right; }
.link.last-clicked { border: 2px dotted #444442; }
.link .entry { border: 1px solid transparent; padding: 4px; padding-top: 1px; position: relative; top: -2px; }
p.title a { font-family: 'Noto Sans', sans-serif; font-size: 11pt !important; color: #ddddd1; text-shadow: 1px 1px 1px #222; }
.thing .title.click, .thing .title { color: #ddddd1; }
a.title:visited { color: #4a4a4a !important; }
span.domain, span.domain a { font-size: 8pt; color: #555; }

.thing .entry { position: absolute; margin-left: 100px; }
.thumbnail { margin: 0 5px 0 0 !important; }
.thumbnail, .thumbnail img { width: 90px !important; height: auto; border-radius: 4px; }

.nextprev { width: 80px; float: right; margin: 1em; }
.nextprev a { position: relative; top: 1em; }

#header-bottom-right { background: #111; top: 80px !important; }
#userbarToggle { background: #111; border-right: 1px solid #456; }

.side { position: fixed; padding: 8px; background: #121211; border: 2px solid #444442; border-radius: 15px; width: 250px; height: 26px; z-index: 99; overflow: hidden; right: -2px; top: 24px; text-align: center; line-height: 42px; color: #888; opacity: 0.35;  }
.side:hover { width: 275px; height: 300px; opacity: 1.0; }
.side > .spacer { position: relative; top: -4px; }
.side .spacer:before { display: block; font-size: 13pt; font-weight: bold; content: "" }
.side .spacer:first-child:before { content: "search\00a0\00a0-\00a0\00a0login\00a0\00a0-\00a0\00a0post"; }
#search { margin-top: 24px !important; }
#search input[type=text] { width: 256px; height: 24px; background: #333; line-height: 24px; font-size: 16px; padding: 4px 8px !important; color: #aaa; }
.login-form-side input[type=text], .login-form-side input[type=password] { background: #333; width: 120px; margin: 15px 4px; }

/* RES */
.NERPageMarker { background: #222; border: 1px solid #444; }
.expando-button { background-color: transparent !important; }
.keyHighlight { outline: none; background: transparent !important; border: 1px dashed rgba(175,225,245,0.5) !important; }

/* hide all bloat-stuff */
.user, .tabmenu, .rank, .midcol, .goldvertisement, .tagline, li.share span { display: none !important; }

/* comments */
.link.odd { background: inherit; }
.comment, .comment .comment, .comment .comment .comment, .comment .comment .comment .comment, .comment .comment .comment .comment .comment, .comment .comment .comment .comment .comment .comment, .comment .comment .comment .comment .comment .comment .comment, .comment .comment .comment .comment .comment .comment .comment .comment, .comment .comment .comment .comment .comment .comment .comment .comment .comment { background: inherit !important; color: #aaa; border: 1px dotted gray !important; }
.comment .md p > a:visited { color: #659; }
}
