@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("babelio.com") {
    body {
        font-family: verdana !important; /* was: Arial,Helvetica,sans-serif*/
    }
    div#page {
		width: 1280px !important; /* was: 967px*/
	}
    body div#header {
        width: 1280px !important; /* was: 980px */
    }
    input.text, input.file, textarea, select {
        font-family: verdana !important; /* was: Arial,Helvetica,sans-serif*/
    }

	
	/* page organisée en 2 colonnes avec celle de gauche qui prend 2/3 de la page */
	#page .page_deux_tiers .colonne_gauche {
		width: 968px !important; /* was: 645px*/
	}
	
	/* page du profil */
	#page .page_deux_tiers  .bloc .colonne_gauche {
		width: 178px !important; /*was: 178px */
	}
	#page .page_deux_tiers  .bloc .colonne_droite {
		width: 771px !important; /* was: 441px */
	}
	
	/* bibliothèque */
	#page .liste_t7 .liste {
		width: 1233px !important; /* was: 920px */
	}
	#page .liste_t7 .liste table thead th:nth-child(3) {
		width: 30%; /* was: auto */
	}
	#page .liste_t7 .liste table thead th:nth-child(4) {
		width: 20%; /* was: auto */
	}
	#page .liste_t7 .liste table thead th:nth-child(5) {
		width: 20%; /* was: auto */
	}
	#page .liste_t7 .liste table tbody td.titre {
		/*font-size: xx-small;*/
	}
	#page .liste_t7 .liste table tbody td.titre a.titre1 {
		/*font-size: small;*/
	}
	#page .liste_t7 .liste table tbody td.auteur {
		/*font-size: small;*/
	}
	
    /* page d'un livre */
	#page .page_deux_tiers  .bloc .colonne_droite .fiche #contents3 {
		width: auto !important; /* was: 380px */
	}
	#page .page_deux_tiers  .bloc .colonne_droite .fiche #contents3 textarea {
		width: 55em !important; /* was: 30em */
	}
	
	/* page ajoutinfoslivre.php */
	#page .page_demi .colonne_droite, #page .page_demi .colonne_gauche {
		width: 49% !important; /* was: 474px */
	}
	#page .page_demi .colonne_droite textarea, #page .page_demi .colonne_gauche textarea {
		width: 95% !important;
		max-width: none !important; /* was: 440px */
	}
	
	/* bibliothèque : barre de recheche */
	#page li.recherche {
	   background: white !important;
	   border: solid 1px #B8A287;
       border-radius: 5px;
	   width: 33% !important; /* was: 142px */
	   padding-left: 0px !important; /* was: 35px */
	}
	#page .module_t10 .toolbar_top .recherche input.text {
	   width: 95% !important; /* was: 100px */
	   margin-left: 3px !important;
	   margin-right: 3px !important;
	}
	#page .module_t10 .toolbar_top .recherche input.submit2 {
	   left: auto !important; /* was: 151px */
	}
	/* bibliothèque : pagination */
	#page .pagination a {
	   /*font-size: x-small !important;*/
	}

    /* profil, listes à droite */
    #page div.module_t5 ul {
        /*font-size: 0.95em;*/
    }
    
    /* recherche dans le header */
    #header div.recherche {
       width: 520px !important; /* was: 257px */
    }
    #header div.recherche div.forme {
        background: white !important;
        border: solid 1px #B8A287;
        border-radius: 5px;
        width: 520px !important; /* was: 272px */
    }
    #header div.recherche input#searchbox {
        width: 497px !important; /* was: 200px */
    }
    
    #header div.recherche input.submit2 {
        left: auto !important; /* was: 224px */
    }

    /* page ajoutlivres.php */
	#page .liste_t7 table td.titre {
		width: auto !important; /* was: */
