css:
@-moz-document url-prefix("http://www.pogodynka.pl/") {
/* Maciej Bukowski */
div#content-right,
div#content-left,
div#reklama_content_bottom,
div#footer,
div#polskameteo>div.left,
div#polskameteo>div.right,
div#info,
div#logo,
body>div:first-child:not(#contnet-wrapper),
div#header,
div.description2,
div#automaty,
div.description,
div.menuSide,
div.headerelement,
div#barsshade,
#odstep_po_godziny ~ div,
table.info_table > tbody >  tr:first-child + tr,  
th.info2 > span:last-child{
	display: none !important;
}
div.content_title{
	position: relative !important;
  	margin: 0 auto !important; 
  	font-family: Calibri light !important;
  	font-size: 30px !important;
  	color: #00A !important;
}
div#skala>div{
  	width: 80px !important;
  	color: #FFF !important;
  	left: 0px !important;
}
div#skala{
  	width: 85px;
  	background-color: #39F;
  	margin: 0 -3px 0 15px;
  	border-radius: 8px;
}
div#skala>img{
  	opacity: 0;
}
body{
  	background: #FFF !important;
  	margin-bottom: 30px;
}
div#wykres{
  	overflow: visible !important;
}
div#skala, div#wykres{
 	zoom: 0.76 !important; 
  	font-size: 13px !important;
}
table.info_table{
  	zoom:1.4;
  	margin: 0 auto 0 auto !important;
  	display: block !important;
  	background-image: none !important;
  	box-shadow: #CCC 0px 0px 10px 5px;
}
div#content-wrapper,
div#main,
div#content,
div#polskameteo{
  	width: 100% !important;
}
}
