css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://home.myspace.com/index.cfm?fuseaction=user"),
url-prefix("http://home.myspace.com/index.cfm?fuseaction=home"),
url("http://home.myspace.com/Modules/HomeDisplay/Pages/Home.aspx") {
/* Widths & Floats */
#col1_2, .myappsModule, .globalLeaderboard, #footer, .inviteFriendsModule {display:none !important;}
#col1_1 {width:766px !important;}
.module {margin:0 0 10px 1% !important;}
.activityStreamModule {
	float:left !important;
	clear:none !important;
	width:54% !important;}
.superPostModule, .friendSpaceModule {
	float:right !important;
	clear:none !important;
	width:44% !important;}
/* Colors */
body {background:#000 !important;}
.group, .module div {background-color:transparent !important;}
.module {
	background-color:#363636 !important;
	border:none !important;}
.activityItem.odd {background-color:#4f4f4f !important;}
.content * {color:#aaa !important;}
.content a, .content a * {color:#fff !important;}
.content a:hover, .content a:hover * {color:#aaa !important;}
.content h3 {
	border:none !important;
	margin-left:10px !important;}
/* Post Module */
.superPostModule form {border:none !important;}
div.smileyPalette {left:30px !important;}
.smileyPalette * {border-color:#000 !important;}
div.textarea, input[type=text], .qpiInitMode div, .quickPostControl div div div, div.moodList, div.smileyPalette, div.smileySelector div {background-color:#000 !important;}
div.smileySelector {background-color:#eee !important;}
/* Feed */
.activitiesContainer {height:450px; overflow:auto;}
.dropdown ol {background-color:#000 !important;}
/* Friends */
.friendSpaceModule .moduleList li {
	width:24% !important;
	height:auto !important;
	margin:.5% !important;}
.profileimagelink {
	width:100%;
	border:none !important;}
.searchFriends button {width:auto !important;}
/* Search */
#searchContainer_Header, #formSearch_Header {background-color:transparent !important;}
.srchfieldLayer *, .srchfieldLayer {
	color:#fff !important;
	background:none !important;}
#srchMenuContainer, .srchfieldLayer ul {background-color:#000 !important;}
/* Badge */
#viewMyList li, div.viewLink {background-color:#000 !important;}
#viewMyList li a:hover {color:#000 !important}


}
