css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("heise.de") {

body, h1, h2, h3, h4 {
    color               : #ffffff !important;
    background-color    : #333333 !important;
}

/*
 * Hauptseiten 
 */
div#logo_bereich, #container_content, #mitte_links, #mitte_rechts, #mitte, #sitemap, #ho_themenseite_letzte,
ul#navi_bottom,  ul#navi_bottom a, .international,
/* Newsticker */
#breadcrumb,
/* Technology Review */
.teaser_fiver, .teaser, .dossierteaser, #tabbox #tabs-1, #tabbox #tabs-2,
/* Telepolis */
.main, .leftbox, #eventflow,
.pos-spalte, .blog-leiste, .forum, .content-footer, .kasten,
/* Hardware Hacks */
#mitte_mit_linker_spalte,
/* Autos */
.anriss, .anriss_kurz, #mitte_links #top_artikel nav ul, #mitte_links #top_artikel p, #freier_teaser, #freier_teaser .anriss_titel,
/* Developer */
.zweite_ebene, .topteaser, .fussbereich,
/* Foto */
.top_teaser,
/* Netze */
#toolbox #tabs-1, #toolbox #tabs-2,
/* Open Source */
#topteaser, .startseite, #fussbereich,
/* Resale */
.teaser_preisradar, .teaser_karussell,
/* Security */
#mitte_links #newsheader, #mitte_dreispalten #extra_spalte_rechts *,
/* Download */
#softwareverzeichnis #links, #softwareverzeichnis #mitte_start,
#softwareverzeichnis .listen_box_ohne_linie, #softwareverzeichnis .listen_box, .alle_programme, .programmteaser p,
#mitte_liste, .programm_info, #softwareverzeichnis fieldset input,
#mitte_detail, .melden,
x {
    color               : #ffffff !important;
    background-color    : #333333 !important;
    background-image    : none !important;
}

#alertliste li
{
    color               : #fff333 !important;
    background-color    : #333333 !important;
}

a, a b
{
    color               : hsl(0,0%,73%) !important;
}

/*
 * Misc
 */
.heisetopnavi_header a, #themen_aktuell a, .news_option a,
/* Download */
#softwareverzeichnis ul#filter li a, #softwareverzeichnis #navigation li a
{
    color               : #000000 !important;
}

.textfield
{
    background-color    : #505050 !important;
}

div.related_items ul li {
    background-image    : none !important;
}

/*
 * Forums
 */
#mitte_weiss, .forum_navi, #forum_wrap, #forum_ansicht, .forum_content
{
    color               : #ffffff !important;
    background-color    : #333333 !important;
    background-image    : none !important;
}

.forum_subtitle, .beitrag_anz, .thread_user, .thread_date, .posting_date
{
    color               : #ffffff !important;
}

#container_content .hover:hover, container_content .hover_line:hover
{
    background  : #444444 !important;
}

span.quote
{
    color   : #bb0000 !important;
}

.navigation li.aktiv {
    color   : #ffffff !important;
}

}
