css:
@-moz-document domain("tutsplus.com") {

/*Junk*/
.topic-home--non-visual__sidebar, .post__sidebar, .post__comments, .post__footer-sidebar, .post__footer, .post__social-buttons--loaded, .ad, .share-course{
	display: none !important;
}

.post__title-and-body{
	max-width: 100% !important;
	width: 100% !important;
	padding-right: 16px !important;
}

.topic-home--non-visual__posts{
	max-width: 100% !important;
	width: 100% !important;
	padding-right: 0px !important;
}

/*Background*/
.page-content, body{
	background: #181f26 !important;
}

/*Main content*/
.content{
	background: #232d38 !important;
}

.pagination{
	border-top-color: #3B4A5A !important;
}

.pagination__page:hover{
	background: -webkit-linear-gradient(bottom, #181f26, #202a33) !important;
	border: 1px solid #181f26 !important;
	margin: -1px !important;
}

/*Article title + Read more + Browse + Series titles*/
.posts__post-title, .posts--list-large .posts__post-read-more-link, .card-header__browse-link, .promo-block__read-more-link, .syllabuses--list .syllabuses__syllabus-title, .syllabuses--list .syllabuses__syllabus-read-more-link{
	color: #7ea5cc !important;
}

a:hover, a:active, a:focus, .posts--list-large .posts__post-read-more-link:hover, a:hover, a:active, a:focus, .posts--list-large .posts__post-read-more-link:hover, .syllabuses--list .syllabuses__syllabus-read-more-link:hover, .syllabuses--list .syllabuses__syllabus-read-more-link:active, .syllabuses--list .syllabuses__syllabus-read-more-link:focus{
	color: #509be5 !important;
}

/*Borders*/
.posts--list-large .posts__post, .posts--list-small .posts__post, .card-header, .featured-posts__post--secondary, .topic-footer, .topic-footer .card-header{
	border-bottom-color: #3B4A5A !important;
}

/*Content + Top right + Series content + Misc*/
body, .featured-posts__post-title, .post__content, .syllabuses--list .syllabuses__syllabus-summary{
	color: #acb6bf !important;
}

.featured-posts__post:hover .featured-posts__post-title, .posts--grid-medium .posts__post:hover .posts__post-title, .posts--grid-small .posts__post:hover .posts__post-title{
	color: #dae6f2 !important;
}

/*Shadows*/
.featured-posts__post--primary .featured-posts__shadow, .syllabuses--grid .syllabuses__syllabus-preview-image-gradient{
	box-shadow: inset 0px -220px 120px -120px #232d38 !important;
}

.featured-posts__post--visual.featured-posts__post--secondary .featured-posts__shadow{
	box-shadow: inset 0px -65px 30px -30px #232d38 !important;
}

/*Navigation + Dropdown*/
.dropdown-menu__item-link, .topics-menu__topic-link, .topics-menu:hover .topics-menu__dropdown-toggle, .courses-menu:hover .courses-menu__dropdown-toggle, .courses-menu__topic-link{
	background: #181f26 !important;
	color: #acb6bf !important;
}

.courses-menu__topic-link:hover.topic-design{
	background: #330c07 !important;
	color: #9c2515 !important;
	/*border-bottom: 2px solid #9c2515 !important;*/
}

.courses-menu__topic-link:hover.topic-dev{
	background: #00332c !important;
	color: #21a694 !important;
	/*border-bottom: 2px solid #21a694 !important;*/
}

.courses-menu__topic-link:hover.topic-photography{
	background: #0d0733 !important;
	color: #5f49f2 !important;
	/*border-bottom: 2px solid #5f49f2 !important;*/
}

.courses-menu__topic-link.topic-design, .courses-menu__topic-link.topic-dev, .courses-menu__topic-link.topic-photography{
	/*border-bottom: 2px solid #acb6bf !important;*/
}

.courses-menu__free-link:hover{
	background: #2A3744 !important;
}

.dropdown-menu__item-link:hover{
	background: #10151a !important;
}

.dropdown-menu__title, .topic-header__nav-link>a, .topic-header__feed-link__a, .card-header__title{
	color: #acb6bf !important;
}

.topics-menu.open-popup .topics-menu__dropdown-toggle{
	background: #181f26 !important;
	color: #acb6bf !important;
}

.topics-menu__topic-submenu.topic-design{
	background: #802013 !important;
}

.topics-menu__topic-submenu.topic-music{
	background: #1a3580 !important;
}

.topics-menu__topic-submenu.topic-cgi{
	background: #872051 !important;
}

.topics-menu__topic-submenu.topic-business{
	background: #547c20 !important;
}

.topics-menu__topic-submenu.topic-crafts{
	background: #b35000 !important;
}
/*Promo thing*/
.promo-block, .promo-block__title{
	background: #181f26 !important;
	color: #acb6bf !important;
}

/*Grid*/
html.no-touch .posts--grid-large .posts__post:hover .posts__post-title{
	color: #509be5 !important;
}

/*Hub boxes*/
.posts--grid-small .posts__post, .topic-promo-block{
	background: #181f26 !important;
	box-shadow: none !important;
}

.topic-promo-block__site{
	color: #acb6bf !important;
}

/*Misc text*/
.static-links__link{
	color: #acb6bf !important;
}

.static-links__link:hover, .static-links__link--active{
	color: #f0f0f0 !important;
}

.post__content code{
	background: none !important;
	border-color: #181f26 !important;
	color: #5899D3 !important;
}

.tabbed-categories__more-items-link{
	background: -webkit-linear-gradient(bottom, #181f26, #202a33) !important;
	top: -2px;
	border: none !important;
	border-top: 1px solid #3B4A5A !important;
	margin: -1px !important;
	padding: 0px 0px 0px 0px !important;
	min-width: 100% !important;
	text-align: center;
}

.tabbed-categories__tab--active{
	background: -webkit-linear-gradient(bottom, #181f26, #202a33) !important;
	border: 1px solid #3B4A5A !important;
	border-bottom: none !important;
	box-shadow: 0 2px 0 -1px #181f26 !important;
}

.tabbed-categories__tab{
	max-width: 100%;
	min-width: 43%;
}

.tabbed-categories__items, .tabbed-categories__tabs{
	border-bottom-color: #3B4A5A !important;
	min-width: 228px !important;
}

.tabbed-categories__item--active .tabbed-categories__item-link, .tabbed-categories__tab--active .tabbed-categories__tab-link{
	color: #136FD2 !important;
}

/*Read More button + Misc*/
.button, form.recurly button{
	background: -webkit-linear-gradient(bottom, #181f26, #202a33) !important;
	border: 1px solid #181f26 !important;
	color: #acb6bf !important;
}

.button:hover, form.recurly button:hover, .button:active, form.recurly button:active, .button:focus, form.recurly button:focus, .posts--list-large .posts__post-category-link:hover, .posts--list-small .posts__post-category-link:hover, .posts--list-large .posts__post-category-link, .posts--list-small .posts__post-category-link, .post__content .tip-shortcode{
	background: #181f26 !important;
}

/*Syntax*/
.syntaxhighlighter, .syntaxhighlighter .line.alt1, .syntaxhighlighter .line.alt2{
	background: #181f26 !important; /*Not working...*/
}

.syntaxhighlighter .plain, .syntaxhighlighter .plain a, .syntaxhighlighter .gutter{
	color: #acb6bf !important;
}

/*Search bar*/
.topic-header__search-input{
	background: #181f26 !important;
	border-color: #000 !important;
	color: #5899D3 !important;
}

.home-header__search-keywords{
	background: #202a33 !important;
	border-color: #000 !important;
	color: #5899D3 !important;
}

/*Switcher*/
.posts-switcher__link{
	color: #3B4A5A !important;
}

.posts-switcher__item--active .posts-switcher__link{
	color: #E4E4E4 !important;
}


/*course.tutsplus*/
.course__content, .course__sidebar{
	width: 100% !important;
	margin: 0px !important;
}
.nav-sidebar__item--active .nav-sidebar__item-link{
	color: #ACB6BF !important;
}

.courses__course:hover .courses__course-thumbnail{
	background: #E4E4E4 !important;
}

.courses__course:hover, .lesson-video__navigation, .lesson-index__chapter{
	background: #1D2630 !important;
}

.courses__course-info{
	border: 1px solid #3B4A5A !important;
}

.course__description, .course__instructor-name{
	color: #acb6bf !important;
}

.lesson-index__overlay{
	background: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, rgba(0, 0, 0, 0)), color-stop(10%, rgba(35, 45, 56, 0.8))) !important;
}

.lesson-index__lesson{
	background: #181F26 !important;
	border-bottom: 1px solid #3B4A5A !important;
}

hr.lesson-index__course-end-marker, hr.nav-sidebar__separator{
	border-top: 1px solid #3B4A5A !important;
}

.lesson-index__lesson--current .lesson-index__lesson-link, .lesson-index__lesson--current.lesson-index__lesson--watched .lesson-index__lesson-link{
	color: #509BE5 !important
}

.lesson-index__lesson-link:hover, .lesson-index__lesson-link:active, .lesson-index__lesson-link:focus{
	background: #10151A !important
}

.instructor-bio, .buy-course{
	background: #181F26 !important;
}

.instructor-bio--collapsed .instructor-bio__details:after{
	background: linear-gradient(to bottom, rgba(0, 0, 0, 0),#181F26);
}

.start-watching__content{
	background: none !important;
	margin-bottom: 60px !important;
	padding: 0px 350px !important;
}

.start-watching{
	-webkit-box-shadow: none !important;
	-box-shadow: none !important;
}

.buy-course__content{
	background: none !important;
}

.page-content--courses .buy-button__button.button{
	box-shadow: 0 1px 1px rgba(20, 20, 20, 0.5) !important;
}

.user-form{
	background: #1F2833 !important;
}

.user-form__field{
	background: #232D38 !important;
	border-color: #000 !important;
	-webkit-box-shadow: inset 0px 1px 2px rgba(255, 255, 255, 0.1) !important;
	color: #5899D3 !important;
}
}
