css:
@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {


#logo {height: 0 !important;
	width: 0 !important;
	padding-left: 240px !important;
	padding-top: 70px !important;
	background: url(http://oi47.tinypic.com/xfrqwy.jpg) no-repeat !important;}

div.l-header .tab .tab_notice {
background: #270c39;
}


ol#posts li.notification {

  background-color: #6a006a!important;

  border-top: 1px dotted #600ff !important;

  border-left: 0px solid #600ff !important;

  border-bottom: 1px dotted #600ff !important;

  border-right: 0px solid #600ff !important;

  color: #f600ff !important;

  text-decoration: none;

}

.answer_container {

  background-color: #f600ff !important;

}


body {

background: #1c0024 url('http://oi42.tinypic.com/2po5qp5.jpg') !important;
background-position: right bottom !important;
background-attachment:fixed !important;
background-repeat:no-repeat !important;


}

#posts .notification a:not(.avatar_frame) {

color: rgba(00, 00, 00, 1) !important;

font-weight: bold !important;

text-decoration: none !important;

border-bottom: 0px solid #6c92d3;

-webkit-background-clip: padding-box;

}



#posts .notification blockquote {

  border-left: solid 3px #6c92d3 !important;

}



#posts .notification .avatar_frame {

background-color: #6c92d3 !important;

border: 1px solid #6c92d3 !important;

position: absolute;

top: -1px;

left: -55px;

overflow: hidden;

display: inline-block;

}












.post.is_mine.with_avatar.new_post {

  color: #ffffff !important;

  font-family:courier, monospace !important;

  background: rgba(0,0,0,0.50) !important; 

}




img[src*="http://i46.tinypic.com/25jy439.png"]{

opacity:0.00 !important;  

}


#new_post{

  background-image: url(http://assets.tumblr.com/images/new_post_icons_no_labels.png) !important; 

  background-repeat: no-repeat !important; 

}


#posts .post.new_post .new_post_label {

-webkit-font-smoothing: antialiased;

opacity: .9;

font-size: 14px;

font-weight: normal;

color: #ffffff;

text-decoration: none;

text-transform: uppercase;

text-align: center;

width: 74px;

position: absolute;

}



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

  background: rgba(161, 0, 0, .5) !important; 

}







.selection_nipple:not([class="selection_nipple white"]) {

border-bottom: solid 11px rgba(00,00,00, 0.50)  !important;

}



#content_top {

  width: 0px !important;

  height: 0px !important;




}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

background: none !important;

width: 0px !important;

height: 0px !important;

margin-top: 0px !important;

border-bottom: solid 11px rgba(00,00,00, 0.50) !important;

border-right: 11px solid transparent !important;

border-left: 11px solid transparent !important;

}




#content_bottom {

  width: 0px !important;

  height: 0px !important;









}





#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

  padding-top: 25px !important; 

  padding-bottom: 25px !important; 

   -moz-border-radius: 20px !important; 

}





LABEL {

  color: rgba(0,0,0, 1.0) !important; 

}




#footer {

  display: none !important; 

}



.radar {

  display: none !important; 

}


.selected .following,

.selected .followers,

.selected .settings,

.selected .posts,

.selected .messages,

.selected .drafts,

.selected .queue,

.selected .likes  {

  color: rgba(0, 0, 0, 0.50) !important; 

  background: rgba(0, 0, 0, 0.0) !important; 

}





#new_post .arrow {

  opacity: 0.50 !important;

  margin-left: -1px !important; 

}
}
