css:
@-moz-document domain("derpiboo.ru") {
    body {
        background-color: #151515 !important;
        color: #D8D8D8 !important;
        
    }
    
    p, div.footercol a {
        color: #CCC !important;
    }
    
    #header a[href="/"] {
        color: #EEE !important;
    }
    
    div.metabar,
    div.lightmetabar,
    div.metabar a,
    div.lightmetabar a {
        background-color: #3C3C3C !important;
        color: #CCC !important;
    }
    div.metabar div nav span a:hover,
    div.footercol a:hover,
    div.lightmetabar a:hover {
        color: #AAF !important;
    }
    
    div.lightmetabar  a,
    div.metabar span a,
    div.metabar a[href="/settings"] {
        background-color: #666 !important;
    }
    
    div.comment_info {
        border: none !important;
        background-color: #666 !important;
    }
    
    div.comment_info span, div.comment_info {
        color: #EEE !important;
    }
    
    div.comment_body {
        border: none !important;
        background-color: #BFBFBF !important;
        color: #333;
    }
    
    #navigation, div.metabar div, div.lightmetabar div {
        border-color: #BFBFBF !important;
    }
    
    div.userbox a, #navigation a,
    #navigation div form {
        background-color: #AAA !important;
    }
    
    #navigation div form input {
        background-color: #BFBFBF !important;
    }
    
    div.image, div.imageinfo {
        /*border-color: #BFBFBF !important;
        background-color: #3C3C3C !important;*/
        border: none !important;
        background-color: #000 !important;
        color: #BFBFBF !important;
    }
    
    div.metasection a.voted_up {
        background-color: #5A5 !important;
    }
    
    div.metasection a.voted_down {
        background-color: #A55 !important;
    }
    
    div.metasection a.faved {
        background-color: #AA5 !important;
    }
    
    div.metasection a {
        background-color: #666 !important;
    }
    
    div.image_description {
        background-color: #666 !important;
        color: #EEE !important;
        border: none !important;
    }
    
    div.image_show_tag {
        border: none !important;
        border-radius: 5px;
    }
    
    div.image_show_tag a, div.image_show_tag {
        color: #666 !important;
    }
    
    .pretty-table th, .pretty-table tr:nth-child(2n+1) {
        background-color: #999 !important;
    }
    
    .pretty-table tr:nth-child(2n+1) {
        color: #111 !important;
    }
    
    div.dnp-warning,
    .important-block,
    .box.warning {
        background-color: #A55 !important;
        border-bottom-color: #A55 !important;
    }
    
    .pretty-table-td-pos, .error, .important-block, .notice, .warning, .warning p {
        color: #333 !important;
    }
    
}
