css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
#home_button {
    padding-right: 10px !important;
}
#home_button a {
    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 41px !important;
    padding-top: 37px !important;
    background: url('http://i.imgur.com/CAWy9xO.png') !important;
}
#home_button .tab_notice {
    left: 37px !important;
    right: auto !important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
.new_post_label{
font-size:0px !important;
}
.new_post_label_icon{background-image:none !important}
/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
#new_post{
background: url('http://i.imgur.com/3XNUBjm.png') center no-repeat !important;
background-color:#fff !important;
}
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:70px;
    padding-right:250px;
    opacity: 0;
}
}

@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
body {
background: url('http://i.imgur.com/s9iPfQP.png') bottom left #fbfcfd no-repeat fixed !important;
}

.radar .radar_tumblelog_label .name, .radar .radar_tumblelog_label .subheading {
color:#000;
}

.avatar_and_i {
background:transparent;
border-radius:10px;
}

div.l-header {
background: #3b352c !important;
border-radius: 15px !important;
border: 1px outset #e89820;
margin-top: -8px !important;
margin-bottom: 20px !important;
padding-bottom: 0px !important;
box-shadow: 0px 10px 8px -12px #e89820 !important;
height: 60px !important;}

ol#posts li blockquote,
body.mceContentBody blockquote {
margin-left: 0 !important;
margin-right: 0 !important;
padding-left: 10px !important;
border-width: 4px !important;
border-color: #dadada!important;
}

ol#posts li.notification.single_notification {
background-color: #e89820;
font-size: 11px;
}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: transparent !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: transparent !important;
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border-radius: 20px;
margin: auto;
padding: 40px auto;
}

h1 {
color: #a26a16;

}

.no_posts_found, h1.dashboard_header {
color: #a26a16;
}

img#content_top, img#content_bottom {
display: none;
}


#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {
background: transparent;
}

#nav .nav_item.active {
background: transparent !important;
}

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background: transparent !important;
}

.notification  {
background: #3b352c !important;
border:none !important;
font-size: 11px;
}

.notification :link, .notification :active, .notification :visited {
color: #a26a16!important;
text-decoration:none !important;
border:none !important;
}

#posts .notification .hide_overflow {
color: #e89820 !important;
}

#posts .notification blockquote {
color:#000;
border-left: solid 3px #667B90;
}

ol#posts li.post .permalink {
background: transparent;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
background: transparent ;
border: solid #000000;
}

ol#posts li.post .post_avatar{
background: #000;
}

.post_avatar, .avatar_frame, .sub_avatar {
border: 0px solid #0034c9 !important;
background-color:#000 !important;
}

.avatar_frame:hover {opacity: 0.7;}

#posts .notification .avatar_frame {
background-color: #000;
border: 1px solid #000;
}

#right_column .controls_section .with_subtitle small, .a {
color: #e89820;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
border-top: 1px solid #e89820;
border-bottom: 1px solid #e89820;
}

#right_column .controls_section {
border-top-width: 0!important;
background: #3b352c;
}

form.dashboard_options_form {
background-color: transparent ;
border-bottom-left-radius: 10px 10px;
border-bottom-left-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-right-radius: 10px 10px;
border-top-right-radius: 10px 10px;
color: #ec9a0c;
display: block;
font-size: 13px;
margin-bottom: 25px;
padding: 15px 22px;
}

form.dashboard_options_form .option_container {
border-top: 1px solid #d29b9b;
}

.no_posts_found, h1.dashboard_header {
color: #402a08;
}

.answer_container {
background-color: #000000;
border-bottom: 0px solid #ec9a0c;
}

#nav .nav_item .nav_menu {
background-color: #000000;
border-bottom: 2px solid #ec9a0c;
}

#nav .nav_item .nav_menu a {
border-top: 1px solid #ec9a0c;
}

#previous_page_link, #next_page_link, .add_and_remove,
.mass_editor, .MissingE_marklist_title, .customize,
.recessed, .answer_avatar, .flat, .dashboard_options_form,
#auto_pagination_loader{
background:#3b352c !important;
border-top: 0px;
} 

#right_column .dashboard_nav_item a:hover {
color: #e89820;
}

#right_column .dashboard_nav_item a {
color: #a56161;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a:hover {
color: #bb5959;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a {
color: #a56161;
}

#right_column .dashboard_nav_item .dashboard_nav_title {
color: #a56161;
}

#right_column {
color: #a56161;
}

#right_column a#dashboard_switch_blog_menu_current {
color: #f2f2f2;
}

#nav .nav_item {
color: #000000;
}

#nav .nav_item:hover {
color: #000;
}

.new_post_notice {
background-color: #000;
}

.new_post_notice img {
opacity: 0;
}

#search_box input {
border: 1px solid #fff;
}

#footer {
color: #3b352c;
}

#footer a {
color: #3b352c;
}

#dashboard_controls_radar_media_fold {
opacity: 0;
}

.radar .radar_controls_buttons {
background: #000;
}

#edit_post_controls #slug_url {
color: #ae9999;
}

h2 .optional {
color: #ae9999;
}

h2 {
color: #ae9999;
}

#promos .promo .tidbit {
color: #ae9999;
}

#edit_post_controls .advanced_option label {
color: #ae9999;
}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 4px #3b352c !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px #3b352c !important;}

.arrow {
background: none !important;
width: 0px !important;
height: 0px !important;
border-right: solid 9px #3b352c !important;
border-bottom: 9px solid transparent !important;
border-left: 2px solid transparent !important;
border-top: 9px solid transparent !important;
margin-top: 0px !important;
}

div.return_to_top_icon{	background: #3b352c !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 35px #e89820 !important;
border-right: 20px solid transparent !important;
border-left: 20px solid transparent !important;
}

.nipple {
background: none !important;
width: 0px !important;
height: 0px !important;
border-right: solid 6px #3b352c !important;
border-bottom: 6px solid transparent !important;
border-left: 0px solid transparent !important;
border-top: 6px solid transparent !important;
margin-top: 0px !important;
}
.nipple.border {
background: none !important;
width: 0px !important;
height: 0px !important;
border-right: solid 6px #3b352c !important;
border-bottom: 6px solid transparent !important;
border-left: 1px solid transparent !important;
border-top: 6px solid transparent !important;
margin-top: 1px !important;}

.post_question {
font-family: 'Courier new' !important;
font-size: 16px !important; 
background: #3b352c !important;
color: #e89820 !important;
}

.post_question_nipple {
background: transparent !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-top: solid 15px #e89820 !important;
border-right: 10px solid transparent !important;
border-left: 10px solid transparent !important;
}

.follower {
background: solid #e89820;
}

.follower.alt {
background: #e0ecee;
}

#right_column:after{
background: transparent !important;
}

#left_column #posts .post .permalink{ background:transparent!important; }
}
