css:
div#feedlyFrame { width : 85% !important;}
div#feedlyPart { width : 98% !important;}
div#feedlyPage, div#mainArea { width: 100% !important;}
div#sideArea
{ display: none !important; }
.entryHolder {
width:100% !important;
max-width: 100% !important;
}
.u100Entry { width: 100% !important; }
.u100Entry.selectedEntry { width: 100% !important; }
.area { margin-left:100px !important }

div#feedlyTabs { width:400px !important; }
div#feedlyTabsHolder { width:400px !important; }
div#mainArea { width:100% !important; }
BODY.home { background: url(http://i.imgur.com/toc2m27.jpg) repeat fixed !important; }

#floatingbar {
position: fixed;
margin-left: 100;
line-height: 24px;
z-index: 10;
padding-top: 10px;
padding-left: 33px;
padding-right: 33px;
width: 918px;
background-color: #FFF;
}
.dark #feedlyTabsHolder .nonEmpty {
font-weight: normal;
color: rgba( 255, 255, 255, 0.65 );
}
#feedlyTabs .label {
cursor: pointer;
font-size: 11.4px;
text-align: left;
text-transform: uppercase;
white-space: nowrap;
text-overflow: ellipsis;
max-width: 140px;
overflow: hidden;
text-transform: uppercase;
}
