css:
@namespace url(http://www.w3.org/1999/xhtml);
    
@-moz-document domain("free-tv-video-online.me") {

    * {
     background: transparent !important;
    }
    
    body {
     background: transparent url('https://www.googledrive.com/host/0B3CxF6MSP9BbYXozdDNIOFNWbG8/ptv/simple_dashed.png') repeat !important;
     color: #eee !important;
     font-size: 100% !important;
     font-family: "Helvetica Neue", Helvetica, sans-serif !important;
    }
    a.date {
      background: #222 !important;
      border: 1px solid white !important;
      border-radius: 5px !important;
    }
    body a[href="http://www.free-tv-video-online.me"]:first-child {
     background: transparent url('https://www.googledrive.com/host/0B3CxF6MSP9BbYXozdDNIOFNWbG8/ptv/header.png') no-repeat !important;
     height: 37px !important;
     width: 297px !important;
     display: block !important;
     position: relative !important;
     top: 20px !important;
    }
    a.epp {
      color: white !important;
    }
    
    td[width="160"] { display: none !important; }
    
    span.style3 {
      text-transform: uppercase !important;
      position: relative !important;
      top: -50px !important;
      font-size: .7em !important;
    }
    span.style3 a[href="http://www.free-tv-video-online.me"] {
      background: none !important;
    }
    a.dloadt { display: none !important; }
    td.mnllinklist:hover {
      transition: background 1s;
      -moz-transition: background 1s; /* Firefox 4 */
      -webkit-transition: background 1s; /* Safari and Chrome */
      -o-transition: background 1s; /* Opera */
      background: #333 !important;
    }
    td.mnlcategorylist sub {
      background: #333 !important;
      color: white !important;
      border: 1px solid white !important;
      border-radius: 5px !important;
      font-weight: bold !important;
      text-transform: uppercase !important;
      vertical-align: 1px !important;
      padding: 3px !important;

    }
    td.mnlcategorylist:hover sub {
      transition: color 1s ease;
      -moz-transition: color 1s ease; /* Firefox 4 */
      -webkit-transition: color 1s ease; /* Safari and Chrome */
      -o-transition: color 1s ease; /* Opera */
      color: yellow !important;
    }
    td.tleft a {
       text-decoration: none !important;
       
    }
    td.tleft a:hover {
       text-decoration: underline !important;
    }
    
    td.mnlhighlightheading {
      color: white !important;
    }
    td.today {
      background: #333 !important;
      border-top: 2px solid white !important;
      border-left: 2px solid white !important;
      border-right: 2px solid white !important;
    }
    td.ftoday {
      background: #333 !important;
      border-left: 2px solid white !important;
      border-bottom: 2px solid white !important;
      border-right: 2px solid white !important;
    }
    a {
      color: #3299bb !important;
    }
    span.style3 a:hover {
      text-decoration: underline !important;
      color: #ccc !important;
    }
    
    form#cse-search-box {
        display: none !important;
    }
    img[alt=mid-logo-free-tv-show-series-movies-online-streaming] {
     
        display: none !important;
    }
    div#fancybox-content {
        position:relative;
        z-index:9999999;
    }

    div#fancybox-content td {
        background: #444 !important;
    }
    a#fancybox-close {
        z-index:999999999 !important;
        background: transparent url('https://www.googledrive.com/host/0B3CxF6MSP9BbYXozdDNIOFNWbG8/ptv/close30.png') no-repeat !important; 
    }
}
