css:
@-moz-document domain("www.4gamer.net") {
div.header, div.V2_global_navi, div.left_contents, div.right_contents,
div.contents_header, #SOCIALBOOKMARK_BAR, div.related_heading,
ul.title_info, div.keyword, .copyright, div.contents_bottom_pr,
div.article_eof, div.whats_new, ins, div[class^=footer]
{ display: none !important; }

body {
  background: none !important;
  background-color: #f4f4f4 !important;
}
div.finding, div.finding * {
  background: none !important;
  color: #ccc !important;
  padding: 0 !important;
}
.contents {
  width: 800px !important;
  margin: 2em auto !important;
}
.main_contents {
  float: none;
  width: auto;
  padding: 2em 40px !important;
  box-shadow: 0 1px 3px #ddd;
  border-radius: 4px;
}
.main_contents div.finding {
  width: auto;
}
.main_contents .container {
  width: auto;
}
.main_contents .container h1 {
  width: auto;
  margin: 2em auto;
}
.main_contents .container .author {
  width: auto;
}
.main_contents .container .maintxt {
  line-height: 1.7em;
}
h2.emphasis a {
  background: none !important;
  border: none !important;
}
#MEDIANEWS_box {
  width: auto;
}
#MEDIANEWS_box .media_4gamer, #MEDIANEWS_box .media_dengeki, #MEDIANEWS_box .media_famitsu {
  width: auto;
}
}
