css:
@-moz-document domain('memrise.com'), url-prefix('http://www.memrise.com/') {

    #page-head.vanilla, .full-width.alternate, html, body,
    .mem-view{
        background-image: none !important;
    }
    
    * {
        background-color: #000000 !important;
        /*background-image: none !important;*/
        color: #cccccc !important ;
        border-color: #3e3e3e !important;
        text-shadow: none !important;
    }

    .garden-box.end_of_session .memstreamheader,
    .thing-show .columns .show-more-link{
        background-image: none !important;
    }

    h1.center{
        color: black !important;
    }
    
    #progress-bar .bar , #garden-timer, #circle{
       background-color: rgb(23, 87, 37) !important;
       color: #cccccc !important ;
       border-color: white !important;
       position: absolute !important;
       top: 0px !important;
    }
       
    .right-area
    .circle,
    .frontage,
    .garden-timer,
    .points animated,
    .streak{
        /*position: absolute !important;
        top: 0px !important;
        z-index: 150 !important;*/
        background-color: transparent !important;
        background-image: url("http://static.memrise.com/garden/img/ui/circle-frame@2x.png?17d8c9") !important;
        background-position: center !important;
        background-repeat: no-repeat !important;
        background-size: 100% auto !important;
        /*width: 100% !important;
        height: 100% !important;*/
        color: white !important;
        /*color: transparent !important */
        border-color: white !important;
    }
    
    #sm2movie, .movieContainer sm2_debug flash_debug{
        /*top: 0px !important;
        z-index: 150 !important;*/
        background-color: transparent !important;
        background-image: none !important;
        background-position: center !important;
        background-repeat: no-repeat !important;
        background-size: 100% auto !important;
        /*width: 100% !important;
        height: 100% !important;*/
        color: transparent !important;
        border-color: black !important;
        opacity: 0 !important;
        /*display: none !important;*/
    }
    
    .btn-group{
        opacity: 100 !important;
    }


    a:link  {
        color: lightgrey !important ;
    }

    a:active {
        color: #ffffff !important  ;
    }


    a:visited  {
        color: #999999 !important ;
    }


    a:hover {
        color: lightgrey !important;
    }
    
    #page-head, .course-actions{
        background-image: none !important;
    }
}
