css:
@-moz-document url-prefix("https://ascii.jp/elem/") {
#header, #sideR, #sideL, .pickwrap,
#artAds, #footer, p.returnCat, h5.social, h5.social+div,
h5.related, h5.related+ul, .pages:first-child
{ display:none !important; }

#jpWrap #container {
  width: 760px !important;
}
#container {
  float: none !important;
  margin: 2em auto !important;
  width: auto !important;
  box-shadow: 0 0 4px #ddd;
  border-radius: 4px;
  background: #fdfdfd !important;
}
body#vr #jpWrap #mainC {
  width: auto !important;
  padding: 0 40px 10px;
}
#mainC {
  float: none !important;
  margin: 0 auto !important;
}
#jpWrap {
  width: inherit !important;
  border: none !important;
  background: none;
}
body {
  background: #f4f4f4 !important;
}
#mainC p, #mainC dd {
  font-size: 110% !important;
  color: #444 !important;
  line-height: 1.6 !important;
}
#jpWrap #mainC #articleText .lead, #jpWrap #mainC #articleText .column {
  margin: inherit;
  padding: 0;
}
#articleHead {
  margin: 2em 0 !important;
  border: none !important;
  background-image: none !important;
}
#jpWrap #mainC #articleText {
  padding: 0 50px;
}
#jpWrap #mainC #articleText h2 {
  margin: 60px -20px 25px;
}
#jpWrap #mainC #articleText h3 {
  margin: 40px 0 20px !important;
}
#jpWrap #mainC div.sbmV3 {
  background: none !important;
}
#jpWrap #mainC #articleText p.wpCodeTitle {
  width: auto;
  color: #fff !important;
}
#jpWrap #mainC pre.src {
  width: auto;
}
#articleText pre.code {
  font-family: monospace;
  font-size: 16px;
  line-height: 1.2em;
  border: 1px solid #ccc;
  margin: 0 -51px 2em;
  padding: 1em 2em;
  /*box-shadow: 0 0px 1px #aaa inset;*/
  background: #f5f5f5;
}
div.artdata {
  font-size: 80% !important;
}
h5.serial {
  margin-top: 3.5em !important;
}
}

@-moz-document url-prefix("http://weekly.ascii.jp/") {
#header, #leftside, #rightside, #footer
{ display:none !important; }

body {
  background: #eee;
}
#wrap {
  margin: 2em auto;
  background: none;
}
#contents {
  float: none;
  margin: 0 auto;
  padding: 60px;
  width: 600px;
  background: #fff;
}
#main {
  float: none;
  width: auto;
  background: none;
}
#main .article {
  margin: 20px auto 0;
  padding: 0 0 10px;
  width: auto;
}
}
