css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.wordreference.com") {

body, input, .inputcontainer, #forumapi *{
background-color: #000000 !important;
}

input{
color: #646464 !important;
}

tbody .even{
background-color: #101010 !important;
}

tbody .odd:hover{
background-color: #080808 !important;
}

*{
border-color: #323232 !important;
font-family: calibri !important;
}

.namecell, #header{
color: #e3e3e3 !important;
}

.namecell font span, #header a span{
color: #66bbff !important;
}

.custom-select *{
background-image: none !important;
background-color: #080808 !important;
color: #646464 !important;
box-shadow: none !important;
}

#container, .layout{
background-color: #080808 !important;
box-shadow: 0px 0px 5px #e3e3e3 !important;
}

#container #header, .namecell{
background-color: #323232 !important;
}

#container #search, th{
background-color: #161616 !important;
}

#container .content{
background-color: #080808 !important;
}

#container #footer{
background-color: #161616 !important;
color: #646464 !important;
}

.content *{
color: #969696 !important;
}

#centercolumn{
width: 832px !important;
}

#rightcolumn, #rightcolumn *{
position: absolute !important;
width: 0px !important;
height: 0px !important;
margin-left: 0px !important;
padding: 0px !important;
margin: 0px !important;
color: transparent !important;
}

#listen_widget, #listen_widget *{
background-color: #161616 !important;
color: #969696 !important;
}

.content span{
color: #545454 !important;
}

.content a{
background-color: transparent !important;
color: #e3e3e3 !important;
}

.content a:visited{
color: #66bbff !important;
}

#gamesad{
background-color: #554a00 !important;
color: #e3e3e3 !important;
}

}
