css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(reddit.com), url-prefix(http://www.reddit.com/){

/* Background */

body {

    color: white ;
    background-image: url("http://i.imgur.com/CCjpMVv.jpg")!important;
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
    background-size: cover !important;
    
    }

/*ads */

.infobar.welcome, .link.promotedlink.promoted, .organic-listing, .goldvertisement, #ad_main, main, .adzerk-vote {

    display: none !important;
    
}

/* RES */

#RESShortcutsEditContainer, #RESShortcutsTrash {

    background-color: transparent !important;
    
    }
    
#RESShortcutsSort, #RESShortcutsAdd, #RESShortcutsRight, #RESShortcutsLeft{

    background-color: transparent !important;
    color: #000000 !important;
    
    }

#userbarToggle {

    background-color: transparent !important;
    border-right: none !important;
    
    }
    
.NERPageMarker {

    background-color: #101010 !important;
    color: white !important;
    
    }

.tabmenu li a{

    background-color: transparent !important;
    border-color: white !important;
    
    }
    
.RESDialogSmall > h3 {

    background: -moz-radial-gradient(center, ellipse cover,  rgba(69,72,77,1) 0%, rgba(0,0,0,1) 100%) !important; 

}

.RESCloseButton{

      background-color: #101010 !important;
      
      }
      
.RESDialogContents{

    color: #FFFFFF !important;
    background-color: #353535 !important;
    
    }

#RESSubredditGroupDropdown{

    background-color: #252525 !important;

}

#progressIndicator, .neverEndingReddit {

   background: none !important;
   background-color: transparent !important;

}
        
/* MISC */

#header{

    background: -moz-linear-gradient(top, rgba(149,149,149,1) 0%, 
    rgba(13,13,13,1) 46%, rgba(1,1,1,1) 50%, rgba(10,10,10,1) 53%, 
    rgba(78,78,78,1) 76%, rgba(56,56,56,1) 87%, rgba(27,27,27,1) 100%);
    border-bottom: 1px solid white !important;
}

#header-bottom-right, #sr-header-area {

    background-color: transparent !important;
    
    }


a, .md, .md p, .usertext {

color: #CAC9C9 !important;

}

.imgScanned {

    color: #00BFFF !important;
    
    }

.title{

    color: #CAC9C9 !important;
    
    }
    
.md, .usertext, .usertext-body, .usertext-body ol{

    
   background-color: transparent !important;
    
    }

.side, .spacer {

    background-color: transparent !important;

}

.keyHighlight {

    background-color: #101010 !important;
    
    }

.morelink{

    background-color: #252525 !important;
    background-image: none  !important;
    right: -15px !important;
    width: 250px !important;
    
    }

.nub {

    background-color: transparent !important;
    right: -250px !important;
    position: relative !important;
    top: -30px !important;
    
    }

.titlebox{

    color: grey !important;
    
    }

#title-field, .roundfield {

    background-color: #454545 !important;
    
    }

#thing, .link, .linklisting .link, .link.odd, .link.odd, .link.comments, .link .entry, .entry, .keyHighlight {

    background-image: none !important;
    background-color: transparent !important;
    border: none !important;
    outline: none !important;

    }
   
 .commentarea, .comment.noncollapsed, .comment.comment, .sitetable .comment, .noncollapsed {
    
    color: transparent !important;
    background-color: transparent !important;
    background-image: none !important;

    }

.comment .RES-keyNav-activeElement, .content .commentarea .RES-keyNav-activeElement .md {
    outline: none !important;
    background-color: transparent !important;
}

 .infobar {
 
    background-color: #505050 !important;
    
    }

.RESGalleryLabel{

    color: white !important;
    
    }

#sr-drop-down{

    background-color: #252525 !important;
    
    }

/* SideBoxes */

.redditname, .styleToggle, .fancy-toggle-button, .subButtons, .subscribers, .users-online{

    border-color: transparent !important;
    background-color: transparent !important;
    
    }

/* Global Fixes */

.linkinfo{

    color: white !important;
    background-color: #151515 !important;

      }

.linklisting, .linklisting .odd, .side blockquote, .md blockquote {

    background-image: none !important;
    background-color: transparent !important;
    
    }
    
    
div.content {

    border-color: transparent !important;
    background-color: transparent !important;
    background-image: none !important;
    
    }

/* Search */ 

.searchfacets{

    background-image: none !important;
    background-color: #252525 !important;
    
    }
    
.searchpane{

    background-image: none !important;
    background-color: #151515 !important;
    
    }


/* Grippy */

.grippy, .grippy:after{

background: #000 !important;
border-right: 0px !important;

}

.listing-chooser {

background: #000 !important;
color: #000 !important;

}

/* Comment Area Fix*/


.commentarea  .sitetable .thing .comment .comment .comment .comment .comment .comment .comment .comment .comment .comment {

background-color: transparent !important;
}

.commentarea  .sitetable .thing .comment .comment .comment .comment .comment .comment .comment .comment .comment {

background-color: transparent !important;
}

.commentarea  .sitetable .thing .comment .comment .comment .comment .comment .comment .comment .comment {

background-color: transparent !important;
}

.commentarea  .sitetable .thing .comment .comment .comment .comment .comment .comment .comment {

background-color: transparent !important;
}

.commentarea  .sitetable .thing .comment .comment .comment .comment .comment .comment {

background-color: transparent !important;
}

.commentarea  .sitetable .thing .comment .comment .comment .comment .comment {

background-color: transparent !important;
}

.commentarea  .sitetable .thing .comment .comment .comment .comment {

background-color: transparent !important;
}

.commentarea  .sitetable .thing .comment .comment .comment {

background-color: transparent !important;
}

.commentarea  .sitetable .thing .comment .comment {

background-color: transparent !important;
}

.commentarea  .sitetable .thing .comment {

background-color: transparent !important;
}

.thing .child .entry .noncollapsed {

background: none  !important;
}
       
.commentarea .thing .entry .noncollapsed {
background: none !important;
}
    
.commentarea .RES-keyNav-activeElement .md {

background-color: transparent !important;
    }
    
/*-----*/
}
