css:
@-moz-document domain("ibm.com") {
/* GLOBAL, BIG RULE */
body { background: #EEE; text-align: auto; font-size: 14px; }


/* WIDTH */
#ibm-top { width: 980px; background: #fff; }

div.ibm-landing-page #ibm-content-main { width: 710px; }

#dw-masthead-top-row,
.ibm-ribbon-pane-dw,
#ibm-universal-nav-dw,
#dw-summary-article,
#dw-footer-top-row,
.ibm-ribbon-pane-dw,
#ibm-footer-module-dw {
  width: auto;
}

.dw-footer-columns { width: 1000px; margin: 0 auto; }

div.ibm-landing-page #ibm-content-sidebar,
#ibm-content-sidebar { width: 250px; }



/* BG, COLOR, BORDER */
#dw-masthead-top-row { background: #000;}

#ibm-universal-nav-dw,
#dw-footer-top-row { background: #E07227; border: none; }

#dw-summary-article { background: #EFEFEF; }

#ibm-footer-module-dw { background: #FFF9E1; }


/* FONT */
code,
#ibm-content-main p,
#ibm-content-main .ibm-container ul { font-size: 1em !important; }

#dw-summary-article p,
#ibm-content-main .ibm-container a strong,
#ibm-content-main .ibm-container a b { font-size: 0.95em; }

.displaycode,
.displaycodeliquid { font-size: 14px; }

#ibm-content-main p { line-height: 1.5; }

#ibm-content-sidebar ol,
#ibm-content-sidebar ul.ibm-bullet-list { font-size: 0.83em; }

#ibm-content-main table th, #ibm-content-main table td, #ibm-content-main table.
ibm-results-table caption, #ibm-content-main table.ibm-data-table caption {
  font-size: auto;
}

h1,
.atitle,
.smalltitle { font-family: "Hiragino Kaku Gothic Pro W3", Meiryo, "メイリオ", "MS UI Gothic", arial, sans-serif }




.ibm-container ul li { margin : .5em 0; padding: 0; line-height: 1.5; }
  
#ibm-content-main p.ibm-ind-link.ibm-back-to-top a.ibm-anchor-up-link {
  background: none;
  display: inline-block;
  border: 1px solid #009;
  text-align: center;
  padding: 5px 20px;
  background-image: -webkit-linear-gradient(#55f, #55c);
  background-image: -moz-linear-gradient(#55f, #55c);
  background-image: linear-gradient(#55f, #55c);
  color: #fff;
}

#dw-summary-article p { margin-top: 0.25em; }
p:empty { display: none; }
}
