css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://xianguo.com/reader") {

/*
Xianguo Reader Simple Style
ver 1.7
by forumz
2013-10-17
*/

    .digg_num, .digg_num_highlight, .item_header > .header_right > .digg, .item_header > .header_right > .favorite{
        display:none !important;
    }
    
    .item_header .header_left{
        width:90% !important;
    }
    
    .item_header .header_right{
        width:90px !important;
        padding-right:5px! important;
    }

    .header_left .feed_title{
        width:10% !important;
    }
    
    .header_left .title_pannel{
        width:85% !important;
    }
    
    #contextPannel > a{
        display:none !important;
    }

    #SidePannel{
        background:#EBEFF9 !important;
    }
    
    .folder_ul:hover, .feed_ul:hover{
        background-color: #8DD0FF !important;
    }
    
    .channel_item_selected{
        background-color: #7DC0FF !important;
    }
    
    #itemList_header > div:first-child , #listPannelTop, .setting_header{
        background:#fff url("data:image/gif;base64,R0lGODlhAgAgAKIAAB1eujSC6iB25kSK6zqF6kqN6yF55UKE5iH5BAAHAP8ALAAAAAACACAAAAMSaFbcziPKSKqtIQfFu/8fIAIJADs=") repeat-x !important;
    }
    
    #page_title, #page_title a, .setting_header span{
        margin:0 !important;
        color: #fff !important;
    }
    
    #listPannelTop span{
        color: #fff !important;
    }
    
    #header, #header + div, #header-wrap, #header-wrapper{
        height:0px !important;
    }
    
    #header div.hdr-bar > a, #header-wrapper > div:first-child, #header-nav{
        display:none !important;
    }

    #header div.hdr-bar div.hdr-account div.option a.hdr-link{
        line-height:30px !important;
        padding:0 10px  !important;
    }
    
    #header div.hdr-bar div.hdr-account{
        right:120px !important;
    }
    
    #header div.hdr-bar div.hdr-account div.option span.down{
        display:none !important;
    }
    
    #header div.hdr-bar div.hdr-account, #header div.hdr-bar{
        height:0 !important;
        line-height:0 !important;
    }

    #header div.hdr-bar div.hdr-account ul.user-ops-menu{
        top:30px !important;
    }

    #header div.hdr-bar{
        float:none !important;
        padding:0 !important;
        position: absolute !important;
        top: -2px !important;
        right: 80px !important;
    }
    
    #header div.hdr-bar .br-logo{
        display:none !important;
    }
    
    #header div.hdr-bar a.hdr-link{
        display:none !important;        
    }

    #header div.hdr-account a.hdr-link{
        display:inline !important;
    }

    #header div.hdr-bar .hdr-link{
        color:#e0e0e0 !important;
        text-shadow:none !important;
    }        
    
    #header div.hdr-bar .hdr-link:hover{
        color:#fff !important;
    }

    #context_manageButton .option_button{
        color: #ffffff !important;
    }
    
    .spliter_hide{
        width:2px !important;
        background: none !important;
    }
    
    .item_title_mode{
        padding:8px 0 8px 0 !important;
    }

    .itemPannel_hr{
        background-color:#C0C0C0 !important;
    }
    
    #Sublist .folder_preImg_collapse, #Sublist .folder_preImg_expand{
        opacity:0.6 !important;    
    }
    
    .folder_preImg_collapse, .folder_preImg_expand{
        width: 20px !important;
        height: 20px !important;
        background:url("data:image/png;base64,") no-repeat !important;
        padding:0 2px 0 2px !important;
        overflow:hidden !important;
        margin:2px 3px 0 15px !important;
    }   

    #Sublist .channel_item_selected .folder_preImg_expand, #Sublist .channel_item_selected .folder_preImg_collapse{
        opacity:1 !important;
    }
        
    .feed_favicon{
        background:url("data:image/png;base64,") no-repeat !important;        
    }    

    .feed_ico_li{
        margin:0 3px 0 10px !important;
    }
    
    #page_icon{
        margin-left: 10px !important;
        padding-right:0 !important;    
    }

}
