.media {overflow: visible !important}
.entry .likes {background-position: right top !important}
.entry.private .info {padding-left: 0 !important; padding-right:14px !important;}
.entry.private .info, .entry.direct .info {background-position:right center !important;}
.entry .media img {border: 1px solid #93BAEB !important}
