css:
@-moz-document domain("jdms.dooster.net") {

#sidebar {
  max-width: 100%;
  width: 365px;
  border: none;
  box-shadow: 0px -2px 0px #333, 3px -1px 3px #868686, 3px 10px 18px #868686;
}

ul.sidebar_buttons {
  width: 365px;
}

#sidebar #sidebar_projects li {
  max-width: 100% !important;
  width: 100% !important;
}

#sidebar_projects li:nth-child(even), #sidebar .sidebar_tl_menu li:nth-child(even) {
 background: rgba(255,255,255,.1); 
}

#sidebar_projects li:nth-child(odd) a, #sidebar .sidebar_tl_menu li:nth-child(odd) a {
 color: #eee; 
}

#sidebar #sidebar_projects li:nth-child(even) a, #sidebar .sidebar_tl_menu li:nth-child(even) a {
 color: #eee; 
}

#sidebar_projects li:nth-child(odd), #sidebar .sidebar_tl_menu li:nth-child(odd) {
 background: rgba(255,255,255,.05); 
}

#sidebar_projects li:hover, #sidebar .sidebar_tl_menu li:hover {
 background: rgba(255,255,255,.4); 
}

.maincontent { margin-top: -1px; }

div.summary-box {
  margin-left: 318px !important;
}

#sidebar .sidebar_tl_menu .tasklist {
  width: 310px;
}

#sidebar .sidebar_tl_menu li:hover {
  
}


#dview-tabs .current_project .project_name {
  max-width: 100% !important;
}


/* Task Edit Popup Dialog Window */
#task_edit_dialog, div[aria-labelledby=ui-dialog-title-task_edit_dialog] {
 width: 1006px !important;
 top: 100px !important;
}

}
