css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("whoisi.com") {

body {
font-family: sans-serif !important;
font-size: 0.9em !important;
padding: 12px;
}


.logo-header {
margin: 0 24px;
}

#nav-sidebar {
font-size: 0.9em;
line-height: 1.4;
margin: 12px;
position: fixed !important;
}


div.twitter-entry, div.linkedin-entry {
line-height:1.3;
width:32em !important;
margin: 0 0 24px !important;
}

div.link-collection, div.weblog-entry, div.weblog-summary, div.twitter-collection, div.linkedin-collection {
margin-bottom: 1em;
margin-left: 22px;
margin-top: 6px;
font-size: 120% !important;
}

div.link-collection-item, div.url-pick {
font-size:0.8em;
margin: 0 0 48px 38px !important;
}

div.link-collection-item > img+a {
font-weight: bold;
color: #555;
font-size: 110%;
}

div.link-collection-item > a,
.link-action a {
text-decoration: none;
color: #777;
padding: 0;
}

.link-collection-item > img {
vertical-align: middle;
padding: 0;
margin: 0 4px 0 3px;
position: relative;
bottom: 2px;
}

.link-action {
margin-left: 2ex;
}
body > div > .link-action {
visibility: hidden;
}
body > div:hover > .link-action {
visibility: visible;
}


.timestamp a {
text-decoration: none;
color: #aaa !important;
margin-left: 1ex;
}


body > .link-collection-item + div:not([class="link-collection-item"]) > a {
padding: 6px 48px 24px;
display: block;
margin-top: -24px;
text-align: center;
font-weight: bold;
letter-spacing: 1px;
font-size: 120%;
}

.footer a {
padding: 0;
}

/* Sort by: */
form + br + div {
margin: 12px 24px 24px;
padding: 0 12px 6px;
border-bottom: 1px solid #999;
font-size: 0.9em;
width: 100ex;
max-width: 60%;
color: #777;
}

form + br + div a {
color: #590 !important;
}

form + br + div + br {
display: none;
}

.footer a,
a:link {
color: #590;
}
#nav-sidebar a,
a.weblog-summary:link {
color: #590 !important;
}
a.weblog-summary:visited {
color: #950 !important;
}

a.weblog-summary:hover,
a:hover {
text-decoration: underline !important;
color: #e30 !important;
}

.link-collection a img {
border: 1px solid #bbb !important;
padding: 1px;
}

.link-collection a:hover img {
padding: 0;
border: 2px solid #e30 !important;
}


}
