@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("instapaper.com") {

body
{
    color: #e2e2e2;
    background-color: #293134 !important;
}

div:not(.dropmenu):not(.button)
{
    background: inherit !important;
}

.dropmenu:not(.button)
{
    background:#3DA1FF/* #68717F*/ !important;
}

h1, .section_header, #story
{
    color: #e2e2e2 !important;
}

#story pre
{
    font-family: "Bitstream Vera Sans Mono", "DejaVu Sans Mono", "Courier New", fixed-width;
    font-size: 0.8em;
}

#story img
{
    opacity: 0.8 !important;
}

#story img:hover
{
    opacity: 1 !important;
}

a
{
    color: #5da1fe !important;
    text-decoration: none !important;
}

a.tableViewCellTitleLink:hover
{
    text-decoration: underline !important;
}

a.bookmarklet
{
    color: #a1a1a1 !important;
    text-decoration: none !important;
}

#foldermanage:hover
{
    background: #576472 !important;
}

.textButton, .menugroup
{
    background: #1F4661 !important;
    color: #3873bb !important;
}

.textButton:hover, .menugroup
{
    background: #29506b !important;
}

.archiveButton
{
    background: #57293A !important;
    color: #cc4444 !important;
}

.archiveButton:hover
{
    background: #613344 !important;
}

.actionButton, .menugroup
{
    border: 0px !important;
}


textarea, input
{
    background: #293134;
    color: #e2e2e2;
}

#controlbar_container
{
    background: #252525 !important;
    opacity: 0.1;
}

#controlbar_container:hover
{
    opacity: 0.8;
}

#title_label a
{
    font-size: 1.5em;
}

.archivebutton, #backbutton, .likebutton, .textcontrolsbutton
{
    border: 0px !important;
}

.colormodebuttons
{
    height: 0px !important;
}

#bookmarkListDeckAd, #bookmarkListDeckAdPlaceholder, .lightmodebutton, .darkmodebutton, .sepiamodebutton
{
    visibility: hidden;
}

#bookmarkListDeckAdPlaceholder
{
    height: 0px !important;
}

}
