@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("allegro.pl") {
    #main-header .user-nav,
    #search-bar {
      padding: 0!important;
    }
    #main-header {
      height: 80px!important;
    }
    #search-bar .cart-status {
      top: 0!important;
    }
    #main-nav,
    #siTitleBar {
      display: none!important;
    }
    .main-breadcrumb {
      padding: 0 0 0 50px!important;
      min-height: auto!important;
    }
    .main-breadcrumb-list,
    #aukroplusBanner,
    #siBidForm2 {
      margin: 0!important;
    }
    .siIcoAS {
      left:0!important;
      top:0!important;
    }
}
