css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nydailynews.com") {

div[class="leaderboard-module"],
div[class="pencil-ad"],
div[class="media-include"],
div[id="nydn-topbar"],
div[class="home-ad-links"],
p[id="nydn-disclaimer"],
div[class="text-ad-links"],
div[class="mod-headline"] {
 display: none !important; }

div[class="pencil-ad"] {
 height:0px;
 width:0px; }

#content-home,
#photo-feature,
#idTabs,
#content-shell {
 margin-top:0px !important;
 padding-top:0px !important; }

div[id="nydn-menu-nav"],
ul[class="nydn-nav"] {
 margin-bottom:0px !important;
 padding-bottom:0px !important;
 margin-top:0px !important;
 padding-top:0px !important; }

div[class='nydn-menu-nav'],
ul[class='nydn-nav'] {
 margin-bottom:-30px !important;
 padding-bottom:0px !important; }

}
