css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://userstyles.org), url-prefix(https://userstyles.org) {
    @font-face {
  font-family: 'Kaushan Script';
  font-style: normal;
  font-weight: 400;
  src: local('Kaushan Script'), local('KaushanScript-Regular'), url(https://themes.googleusercontent.com/static/fonts/kaushanscript/v1/qx1LSqts-NtiKcLw4N03IFhlQWQpSCpzb2Peu3I-Q34.woff) format('woff');
}

    html,body{background: url('http://api.thumbr.it/whitenoise-361x370.png?background=e9e4caff&noise=626262&density=49&opacity=9') !important;}
    #main-header{background: none !important;}
    #main-header h1{font-family: 'Kaushan Script';font-size: 54px !important;}
    #main-header h1 a{color: #C94C46 !important;text-decoration: none !important;}
    #main-header h1 a:hover{text-decoration: underline !important;}
    
    #left-sidebar > *{background: none !important;border: 5px solid #28616A !important;}
    #left-sidebar ul li a{color: #C94C46 !important;font-weight: bold !important;}
    
    #main-article p{;font-weight: bold !important;font-size: 1.2em !important;}
    #main-article p a{color: #C94C46 !important;}
    #front-page-best, #front-page-newest{display: inline-block !important;margin-left:auto !important; margin-right: auto !important;width: 50% !important;}
    #front-page-best h2, #front-page-newest h2{background: #C94C46 !important;}
    #front-page-best ul li, #front-page-newest ul li{list-style: none !important;font-style: italic !important;color: #C94C46 !important;}
    #front-page-best ul li a, #front-page-newest ul li a{color: #C94C46 !important;font-weight: bold !important;}
    #front-page-best ul li:before, #front-page-newest ul li:before{content: '--> ';}
    
    #main-article ul:first-child li a{color: #c94c46 !important;font-weight: bold !important;}
    #main-article ul:first-child li{list-style: none !important;}
    #main-article ul:first-child li:before{content: '- ' !important;}
    
    .author-styles{border-left: 3px solid #c94c46 !important;border-radius: 3px !important;}
    .author-styles tr:nth-child(2n+1){background: #CFCBB4 !important;}
    .author-styles a{color: #c94c46 !important;font-weight: bold !important;}
    section > h2{margin-left: 0px !important;}
    
    #long-description,#additional-info {border-left: 5px solid #c94c46 !important;padding-left: 2px !important;}
    #long-description:before{content: 'Info';margin-bottom:10px !important;font-weight:bold !important;font-size: 22px !important;font-style: italic !important;margin-left:0px !important;display: block !important;position: relative !important;}
    #miscellaneous-info{float: none !important;}
    #miscellaneous-info a{color: #c94c46 !important;font-weight: bold !important;}
    #additional-info a{color: #C94C46 !important;font-weight: bold !important;}
    #additional-info:before{content:'Additional Info';margin-bottom:10px !important;font-weight:bold !important;font-size: 22px !important;font-style: italic !important;margin-left:0px !important;display: block !important;position: relative !important;}
    #stylish-installed-style-installed{height:62px !important;background: #28616A !important;color: #e9e4ca !important;text-decoration:none !important;font-weight:bold !important;padding:3px !important;border-radius: 5px !important;border-bottom:3px solid #153237 !important;border-left:3px solid #153237 !important;}
    
    #control-panel a,#stylish-installed-style-not-installed{background: #c94c46 !important;color: #e9e4ca !important;text-decoration:none !important;font-weight:bold !important;padding:3px !important;border-radius: 5px !important;border-bottom:3px solid #963934 !important;border-left:3px solid #963934 !important;}
    #control-panel a:hover{font-style: italic !important;}
    #show-code button {display: block !important;font-family: 'Kaushan Script';color: #333 !important;background-color: #c94c46 !important;font-weight:bold !important;border-radius: 5px !important;border-bottom:3px solid #963934 !important;border-left:3px solid #963934 !important;border-top: none !important;border-right: none !important;}
    #show-code{background: none !important;}
}
