@-moz-document domain("phandroid.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.news-feed section.recent-news.card .post-topic
{
	text-transform: capitalize!important;
}

ul.navigation,
:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.discussionListItem .title,

html {
	font-size: 19px!important;
}

.discussionListItem .secondRow {
	font-size: 17px!important;
}

.card.sidebar-device h3 a,

section.news-mosaic article h3
{
    line-height: 33px!important;
}

.row .col.t12.s4.m4.l4.item {
    position: relative!important;
	left: inherit!important;
	top: inherit!important;
	transform: none!important;
	box-sizing: border-box!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.ad-header,

[id*='google_ads_']
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

.fs-sticky-slot-element
{
	display: none!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

ul.sub-navigation {
    margin-top: 0px!important;
}

ul.sub-navigation li a {
    padding-left: 12px!important;
}

.primary {
    margin-left: 2rem;
}

.nav-main-wrapper {
    top: 5rem;
}

.news-feed section.recent-news.card .post-topic {
    margin-right: 2rem;
}

/* Margins 
_____________________________________________________________________________________________________*/

.row .col.s4,
.row .col.t12.s4.m4.l4.item,
.nav-main-wrapper {
	margin: 12px!important;
}

.content-inner {
	margin: 0 5rem 0 15px!important;
}

section.single.card article.row h2 {
    margin: 3rem 0px 1rem -20px!important;
}

ul.navigation li a i {
    margin: 1rem .9rem 0 0!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.card,
.content-inner
{
	padding: 1.2rem!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.container-main,
.fr-sidebar-cards .card-reveal,
.fr-sidebar-cards .card.sidebar-device
{
	max-width: none!important;
}

.row .col.t12 {
    width: 90%;
}

.primary {
    width: 85%!important;
}

.row .col.s4 {
    width: 31.33333%!important;
}

.row .col.t12.s4.m4.l4.item,
main.index .sidebar
{
    width: 23%!important;
}

}

@-moz-document domain("phandroid.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.pagination li.active,

a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.css-1w9ntwc.css-1w9ntwc div,
.discussionListItem .secondRow dl,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.date-update span,
.news-feed section.recent-news.card .post-timestamp,

section.single.card span.card-title span.timestamp,
time
 {
	color: #07beff!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}



}

@-moz-document domain("phandroid.com") {
/* Background Settings */

body,
html,
main.index
{
	background: #000!important;
}

.css-5vrrls.css-5vrrls {
    background-color: #000000db;
}

/* Background 
_____________________________________________________________________________________________________*/

#userBar,

.navbar-fixed
{
	background: #060505!important;
	background-color: #060505!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#userBar .navTabs,
#userBar .pageContent,

.collapsible-header,
.discussionListItem,
.nav-menu-wrapper,
.navTabs .navTab.selected .navLink,
.pagination li.active,
.valign-wrapper,

aside,
body,
footer.page-footer,
html,
main,
main.pg,
main.review,
main.search,
main.single,
main.tags,
section.card .card-action,
section.single.card div.tags a,
section.single.card div.tags,
section.team .card-action,
ul.navigation
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.button.primary
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.button.primary::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.button.primary:hover::before
 {
  opacity: 1!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.css-1hy2vtq.css-1hy2vtq:nth-child(2)
{	
	background: radial-gradient(circle, #d90008a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
