css:
@-moz-document url-prefix(http://www.theoldreader.com), url-prefix(https://www.theoldreader.com), url-prefix(http://theoldreader.com), url-prefix(https://theoldreader.com) {
body,
input,
textarea {
  font-family: "iranian sans", tahoma, arial, sans !important;
}

#main .slide .posts {
  direction: rtl;
}

.post {
  padding: 5px 13px 0 10px !important;
}

.post h3 {
  direction: rtl;
}

.post.listview .header .span8 {
  white-space: normal !important;
  height: 1em !important;
  text-align: justify !important;
  direction: rtl !important;
}

.post.unread {
  border-left: 0 none !important;
  border-right: 4px solid #30A67C !important;
  padding-right: 10px !important;
  /*padding-left: 0 !important;*/
}

.post.listview .header .name {
  float: right !important;
  margin-left: 5px;
}

.list-post {
  width: 85% !important;
  margin: 0 auto;
}

.post .header span.label {
  float: left !important;
}

.post .header .span8 {
  float: right !important;
}

.content {
  text-align: justify !important;
  direction: rtl !important;
}

.content p {
  line-height: 170% !important;
}

.post .label {
  float: right;
}

/*
.post .btns {
  text-align: right !important;
}*/

.post blockquote {
  direction: rtl !important;
  text-align: right !important;
}

.post blockquote {
  padding: 3px 15px 0 0 !important;
  border-left: 0 !important;
  border-right: 5px solid;
}

.post .comments blockquote small a {
  float: right;
}

.post .comments blockquote small em {
  padding-right: 5px;
}

.post .comments .new,
fieldset.comment {
  text-align: right;
}

.post .share-note textarea,
.post .comments textarea {
  direction: rtl;
  margin-left: auto !important;
}

}
