css:
@-moz-document url-prefix("http://www.furaffinity.net/"), url-prefix("https://www.furaffinity.net/") {
div #fa_header {
	opacity: 100.00;
}
A:LINK{
	color: #FF0000 !important;
}
A:VISITED{
	color: #FF0000 !important;
}
body{
	background-image:url(http://images4.alphacoders.com/778/77806.jpg); background-repeat:no-repeat;
background-attachment:fixed; !important;
}

    body>div, .maintable, .alt1, .cat, .lead, .alt2 {
        background-image:url(http://images4.alphacoders.com/778/77806.jpg); background-repeat:no-repeat;
background-attachment:fixed; !important;
        color: #CC0000 !important;
    }

.links{
	border-top: 2px solid #CC0000 !important;
	border-bottom: 2px solid #CC0000 !important;
	color: #FF0000 !important;
	background-color: #000000;
}
.links A:LINK{
	color: #FF0000 !important;
}
.links A:VISITED{
	color: #CC0000 !important;
}
.links A:HOVER{
	color: #CC0000 !important;
}
.footer{
	color: #CC0000 !important;
}
.footer A:LINK{
	color: #FF0000 !important;
}
.footer A:VISITED{
	color: #CC0000 !important;
}
.maintable{
	background-color: #CC0000
 !important;  
}
.alt1, .alt1 A:LINK, .alt1 A:VISITED{
	background-color: #000000 !important;
	color: #CC0000 !important;
}


.lead{
	border-top: 2px solid #CC0000
 !important;
	border-bottom: 2px solid #CC0000
 !important;
}
.cat{
	background-color: #000000 !important;
	color: #FF0000 !important;
	border: 2px solid #000000 !important;
}
.alt2{
	background-image:url(http://images4.alphacoders.com/778/77806.jpg); background-repeat:no-repeat;
background-attachment:fixed; !important;
	color: #FF0000 !important;
}
.alt2 A:LINK{
	color: #FF0000 !important;
}
.alt2 A:VISITED{
	color: #CC0000 !important;
}
.button{
	color: #FF0000 !important;
	background-image:url(http://images4.alphacoders.com/778/77806.jpg); background-repeat:no-repeat;
background-attachment:fixed; !important;
	border: 1px solid #CC0000
 !important;
}
.fieldset{
	border: 1px ridge #CC0000
 !important;
}
.hovertable{
	background-image:url(http://images4.alphacoders.com/778/77806.jpg); background-repeat:no-repeat;
background-attachment:fixed; !important;
}
.hovertable_head{
	background-color: #FF0000 !important;
	color: #fff;
	border: 2px solid #FF0000;
}
.hovertable_body{
	background-color: #FF0000 !important;
	color: #FF0000;
}
.thumbnail{
	opacity: 0.7 !important;
}



.innertable{
	background-image:url(http://images4.alphacoders.com/778/77806.jpg); background-repeat:no-repeat;
background-attachment:fixed; !important;
}
.logo{
	background-color: #FF0000 !important; 
}
.tab u{
	background-color: #000000 !important;
	border-top: 1px solid #000000 !important;
	border-left: 1px solid #FFFFFF !important;
	border-right: 1px solid #000000 !important;
	color: #FF0000 !important;
}
.tab a{
	background-color: #000000 !important;
	border-top: 1px solid #CC0000
 !important;
	border-left: 1px solid #CC0000
 !important;
	border-right: 1px solid #CC0000
 !important;
}
.ldot{
	border-left: 1px dotted #CC0000
 !important;
}
.rdot{
	border-right: 1px dotted #CC0000
 !important;
}
.bdot{
	border-bottom: 1px dotted #CC0000
 !important;
}
.tdot{
	border-top: 1px dotted #CC0000
 !important;
}
.menu li ul li{
	border: 1px dotted #CC0000
 !important;
	background-image:url(http://images4.alphacoders.com/778/77806.jpg); background-repeat:no-repeat;
background-attachment:fixed; !important;
}
.menu li ul li:hover{
	border: 1px dotted #CC0000
 !important;
	background-color: #000000 !important;
	color: #FFFFFF !important;
}
.menu li ul li A:LINK{
	color: #FF0000 !important;
}
.menu li ul li A:VISITED{
	color: #CC0000 !important;
}

.container-comment{
	border: 1px solid #CC0000
 !important;
}
.container-comment TD,
.container-comment TH{
	color: #FF0000 !important;
}
.container-comment TH{
	background-image:url(http://images4.alphacoders.com/778/77806.jpg); background-repeat:no-repeat;
background-attachment:fixed; !important;
}
.container-comment TD.cat,
.container-comment TH.lead{
	border: 0px none !important;
}
.container-comment TH.lead H4{
	color: #FF0000 !important;
}
.container-comment TD.cat A{
	background-color: #444 !important;
}
.container-comment TD.cat{
	border: CC0000
 !important;
	background-image:url(http://images4.alphacoders.com/778/77806.jpg); background-repeat:no-repeat;
background-attachment:fixed; !important;
}
div#messagecenter-submissions{
	color: #FF0000 !important;
}
div#messagecenter-submissions div.navigation a{
	color: #FF0000 !important;
	background-color: #000000 !important;
	border: 1px solid #CC0000
 !important;
}
div#messagecenter-submissions div.actions .remove-checked{
	background-image:url(http://images4.alphacoders.com/778/77806.jpg); background-repeat:no-repeat;
background-attachment:fixed; !important;
}
div#messagecenter-submissions div.actions .remove-nuke{
	background-color: #000000 !important;
	color: #FF0000 !important;
}
div#messagecenter-submissions table.checked{
	background-image:url(http://images4.alphacoders.com/778/77806.jpg); background-repeat:no-repeat;
background-attachment:fixed; !important;
}
div#messagecenter-submissions h3.date-divider{
	border-bottom: 1px solid #CC0000
 !important;
}
#description_popup{
	border: 1px solid #CC0000
 !important;
	background-image:url(http://images4.alphacoders.com/778/77806.jpg); background-repeat:no-repeat;
background-attachment:fixed; !important;
}
#description_popup h5{
	background-color: #000000 !important;
	color:  #FF0000 !important;
}
#description_popup span{
	border-top: 1px solid #CC0000
 !important;
	color: #FF0000 !important;
}

.popup_date{
	border-bottom: 1px dotted #CC0000
 !important;
}

div#messagecenter-other ul.message-stream li em {
border-bottom: 1px dotted #CC0000
 !important;
}
div#messagecenter-other ul.message-stream li em{
	border-bottom: 1px dotted #CC0000
 !important;
}

div#messagecenter-other fieldset h3 input{
	background-image:url(http://images4.alphacoders.com/778/77806.jpg); background-repeat:no-repeat;
background-attachment:fixed; !important;
	color: #FF0000 !important;
}

div#messagecenter-other {
                        color: #FF0000 !important;
}

div#messagecenter-other ul.message-stream table.checked{
	background-image:url(http://images4.alphacoders.com/778/77806.jpg); background-repeat:no-repeat;
background-attachment:fixed; !important;
}
div#messagecenter-other ul.message-stream li.checked{
	background-image:url(http://images4.alphacoders.com/778/77806.jpg); background-repeat:no-repeat;
background-attachment:fixed; !important;
}
div#messagecenter-other ul.message-stream li.hover{
	background-color: #000000 !important;
}

div#messagecenter-other ul.message-stream li.section-controls,
div#messagecenter-other #messages-watches ul.message-stream li.section-controls{
	border-bottom: 1px solid #CC0000
 !important;
}

.textarea, .textbox, .listbox, select, textarea, .uploadfield {
          color: #FF0000 !important;
          background: #FFFFFF !important;
}

.no_overflow a:hover, .no_overflow a:link, .no_overflow a:visited, .cat a:hover, .cat a:link, .cat a:visited, .lead a:hover, .lead a:link, .lead a:visited{
background: rgba(0, 0, 0, 0.0) !important;
}
}
