*
===========================================================*/

@-moz-document url("about:home") { 
* {  color:  #aaa !important;  background: black !important; background-image: none !important  }
}

@-moz-document url-prefix(http://), url-prefix(ftp://), url-prefix(file://),
url-prefix(https://), url("about:blank"),  url-prefix("chrome://"),
url-prefix("data"), url-prefix("jar"), 
url("about:support"), url("about:memory"), url("about:home"),url("about:addons"),url("about:plugins"), url-prefix("about:reader")
{

/* this should not be tagged !important as it sometimes interferes with some web sites */
* {  color:  #aaa ;  background-color: black; text-align:justify }

* {border-color: #999 !important; text-shadow: none !important; }


html, body,  th, table, tbody, ul, dl, dt, dd, ol, li, form, iframe, fieldset, label, font, style, span, p,td, div, nav, section, caption, figcaption,
center , header, footer  , article 
{
color:  #888   !important; 
background-color: black !important;
}

div {outline-color: #777 !important;}

cite, cite *, code , pre, pre *, box *, fieldset, blockquote *, blockquote, legend, q
{  color: #ccc  !important; background-color: #001120 !important;} 

::-moz-selection { color: black ! important; background-color: #a6ab00 ! important; }

a:link, * a, a:link * { -moz-appearance: none !important;  background-color: black !important;  color: #4478bb     !important }

strong, big, b, b  *,h1,  h1 *,h2, h2 *,h3, h3 *,h4, h4 *,h5, h5 *,h6, h6 *,h7, h7 *, em, tt, span[style*="font-weight: bold;"]
span[style*="font-style: italic;"], i,
*[font-size="xx-large"], *[font-size="x-large"], stname, ssname, small,  arttitle, abbr, acronym, sup
{ background-color: black !important; color:  #ccc  !important; } 

img, img[src*="svg"] 
{ background-color: #999481 !important; color: black !important}
a > img {background-color: transparent !important;}

hr { background-color: #222   !important; color: #888 !important;} /* hr color not working */ 


/*====== highlighting ==== */

m, span[class="highlight"] ,  h3 > a > em
{background-color: darkcyan !important; color: black !important;}   

/* === mainly for maths display ======== */

math { background: #023 !important; }  
[class*=MathJax], [class*=MathJax] *, [id*=MathJax], [id*=MathJax] * 
{   background-color: #222 !important;color : #aaf !important;   
opacity: 1  !important; }

/* ==== various inputs ==== */

input[type=button], input[type=submit], input[type=reset],  input[type=hidden], button, a.button 
{
background-color:  #012 !important; 
color:  burlywood !important; 
border-color: ButtonHighlight ButtonShadow ButtonShadow ButtonHighlight !important;
}

select, input[type=select]
{ 
background-color: #5d6871 !important; 
color:  #000 !important; 
}

textarea, input[type=textbox], input[type=text], input:not([type="image"]), samp, kbd, var, dfn 
{ 
background-color: #023 !important; 
color: #ccc !important;
border: #aaa  solid !important;
}


input[type=file] { background-color : lightgrey !important; color:black !important}

input[type=password]
{ 
background-color:  #77a7aa !important; 
color: #0000FF !important;
border: blue solid !important;
}

input[type="checkbox"]
{color : burlywood !important;
background-color: #111 !important;
border: #666  solid !important;}  


input[type="checkbox"][disabled]
{background-color: #000 !important;
border: #999 dotted !important;} 

input[type="image"] 
{-moz-appearance: none !important;border:none !important; display: block !important; }


/* ============ canvas ================ */
canvas {background-color: transparent !important; }   


/* a special workaround */
html > div, html > div * {background-color: transparent !important; } 
}
