css:
@-moz-document domain("habrahabr.ru") {
.sidebar_right,
sup a[href="/settings/upgrade/"],
a.logo,
ul.tags,
.subscribe_comments,
.top_banner,
.hubs
{
  display: none !important;
}

#layout
{
  width: 1500px;
}

#layout #header .main_menu
{
  padding-top: 15px !important;
  zoom: 55%;
  padding-left: 800px;
}

#header .search
{
  top: 17px;
}

.content_left
{
  width: 100% !important;
}

.post .content
{
  zoom: 120%;
  text-align: justify;
}

.post .content img
{
  display: block;
  margin: 0 auto;
}

.post .content img[align="right"]
{
  display: block;
  margin: 5px auto;
  padding-left: 10px;
}

.message.html_format 
{
  zoom: 120%;
  text-align: justify;
}

body[style*="background"]
{
  background: none !important;
}

#layout .wrapper
{
  padding: 0 !important;
}

.corporation_head
{
  zoom: 80%;
}
}
