css:
@-moz-document domain("4pda.ru") {
#topnavi, #sidebar, .commentdeleted, .footer_text, #footer
{
  display:none;
}

#header, #page
{
  margin-top: 5px !important;
}

#content, .narrowcolumn
{
  width:99% !important;
}

#inner, #header
{
  max-width: 1600px !important;
}

.entry
{
  font-size: 13pt!important;
}

div.post h2
{
  font-size: 24pt!important;
}

.header_m_links, .header_m_links_c
{
  background-image: none;
  background-color: #13A4F4;
  border: 0px; 
}

ul.dropdown a:link, ul.dropdown a:visited 
{
  color: #FFFFFF;
}

ul.dropdown a[title="Форум"]:link, ul.dropdown a[title="Форум"]:visited
{
  color: #FFFFFF;
  font-weight: bold;
  padding-right: 40px;
}


ul#nav.dropdown a:hover, ul.dropdown a[title="Форум"]:hover 
{
  color: #4373c3;
  text-shadow: none;
}

ul#nav.dropdown a:link 
{
  padding-right: 40px;
}

.submit
{
  width: 75px;
  height: 24px;
}

#s
{
  height: 20px;
}

#content {
  font-size: 1.6em !important;
}
}
