@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.freerepublic.com") {
/*   */
body { background: url('http://i104.photobucket.com/albums/m165/t17o76m/800x1080_zps4e9920a3.gif')
       repeat fixed black !important; }

* { color: rgba(176,196,222,0.66) !important; text-shadow: rgb(0, 0, 0) 0px 0px 4px !important;
    background-color: rgba(0,0,0,0.0) !important; border-color: rgba(0,0,0,0.0) !important; }

a, body.index-page ul.listing .meta .attrib a, a * { color: rgba(255,255,240,0.66) !important; text-decoration: none !important; }

a:visited, body.index-page ul.listing .meta .attrib a, a:visited * { color: rgba(255,255,240,0.66) !important; }

a:hover, a:hover * { color: rgba(255,160,122,0.75) !important; }

            input[type=text], textarea {
  -webkit-transition: all 0.30s ease-in-out !important; 
  -moz-transition: all 0.30s ease-in-out !important; 
  -ms-transition: all 0.30s ease-in-out !important; 
  -o-transition: all 0.30s ease-in-out !important; 
  outline: none !important; 
  padding: 3px 0px 3px 3px !important; 
  margin: 5px 1px 3px 0px !important; 
  border:1px solid rgba(176,196,222,0.66) !important; 
}
 
input[type=text]:focus, textarea:focus {
  box-shadow: 0 0 5px #DCDCDC !important; 
  padding: 3px 0px 3px 3px !important; 
  margin: 5px 1px 3px 0px !important; 
  border: 1px solid rgba(176,196,222,0.66)!important; 
}

}
