css:
@-moz-document domain("pandora.com") {
  body {
    background: hsl(0, 0%, 8%) !important;
  }
  #mainContainer, #company-support-portal {
    background-image: none;
    background-image: linear-gradient(to top, hsl(0, 0%, 8%), hsl(0, 0%, 4%), hsl(0, 0%, 0%));
  }
  #brandingBar, #playerBar {
    background: #000;
  }
  #brandingBar .logosubscriber, #playerBar .highlight {
    background: transparent;
  }
  #skinTab { display: none !important; }
  .slidesBackground img {
    display: none
  }
  .slidesForeground {
    background-image: linear-gradient(to top, hsl(0, 0%, 8%), hsl(0, 0%, 4%), hsl(0, 0%, 0%));
  }
  #company-header {
    background-image: none !important;
    background: transparent !important;
  }
  #playerBar .rightcolumn .info a.playerBarSong {
    font-weight: 500;
  }
  #playerBar .pauseButton, #playerBar .playButton { border-radius: 23px; }
  #playerBar .thumbDownButton, #playerBar .thumbUpButton {
    border-radius: 16px;
  }
  #playerBar .skipButton, #playerBar .skipButton a:link, .skipButton a:visited, #playerBar .skipButton a:hover, #playerBar .skipButton a:active {
    border-radius: 18px;
  }
  #playerBar .pauseButton {
    background: #383f4d url(http://www.pandora.com/img/player-controls/btn_pause.png) no-repeat top left;
  }
  #playerBar .playButton {
    background: #383f4d url(http://www.pandora.com/img/player-controls/btn_play.png) no-repeat top left;
  }
  #playerBar .thumbDownButton {
    background: #383f4d url(http://www.pandora.com/img/player-controls/btn_down.png);
  }
  #playerBar .thumbUpButton {
    background: #383f4d url(http://www.pandora.com/img/player-controls/btn_up.png);
  }
  #playerBar .skipButton, #playerBar .skipButton a:link, .skipButton a:visited {
    background: #383f4d url(http://www.pandora.com/img/player-controls/btn_skip.png) no-repeat -1px 1px;
  }
  #playerBar .skipButton a:hover{
    background:url(http://www.pandora.com/img/player-controls/btn_skip_hover.png) no-repeat -1px 1px;
  }
  #playerBar .skipButton a:active{
    outline:0;
    background:url(http://www.pandora.com/img/player-controls/btn_skip_press.png) no-repeat -1px 1px;
  }
}
